INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (57708, 'Entrectinib', 'Diltiazem', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of entrectinib, which is primarily metabolized by the isoenzyme. Increased exposure to entrectinib may increase the risk and/or severity of adverse effects such as cognitive impairment, mood disorders, dizziness, sleep disturbances, liver enzyme elevations, hyperuricemia, congestive heart failure, edema, myocarditis, QT prolongation, vision problems, anemia, and neutropenia.', NULL, 'Concomitant use of entrectinib with moderate CYP450 3A4 inhibitors should be avoided when possible.', NULL, 'Concomitant use of entrectinib with moderate CYP450 3A4 inhibitors should be avoided when possible. If coadministration is necessary, the dosage of entrectinib should be reduced to 100 mg once daily for adults and pediatrics patients (12 years and older) with a body surface area (BSA) greater than 1.5 m2. Following discontinuation, and after an appropriate washout period of the CYP450 3A4 inhibitor (i.e., 3 to 5 half-lives), the entrectinib dosage taken prior to initiating the CYP450 3A4 inhibitor may be resumed. For pediatric patients with a body surface area of 1.5 m2 or less, concomitant use of entrectinib with moderate CYP450 3A4 inhibitors should be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57784/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.[3] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.[4] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (57709, 'Enzalutamide', 'Diltiazem', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 and/or 3A4 may increase the plasma concentrations of enzalutamide, which is primarily metabolized by CYP450 2C8 to its pharmacologically active metabolite, N-desmethyl enzalutamide, and to a lesser extent by CYP450 3A4.', NULL, 'Pharmacologic response to enzalutamide should be monitored more closely whenever a CYP450 2C8 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary.', NULL, 'Pharmacologic response to enzalutamide should be monitored more closely whenever a CYP450 2C8 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary. Patients should be advised to contact their physician if they experience increased side effects such as seizure, asthenia, fatigue, diarrhea, arthralgia, musculoskeletal pain, paresthesia, hot flushes, peripheral edema, headache, dizziness, insomnia, hematuria, anxiety, and hypertension.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57785/', '[1] "Product Information. Xtandi (enzalutamide)." Astellas Pharma US, Inc, Deerfield, IL.', 1767365204);
