INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (57821, 'Diltiazem', 'Lacosamide', 'Moderate', 'Lacosamide has been shown to prolong the PR interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, calcium channel blockers, digoxin, atazanavir, mefloquine) may result in elevated risk of conduction disturbances and atrioventricular block.', NULL, 'Caution is advised if lacosamide is used concomitantly with other agents that prolong the PR interval, especially in the elderly and patients with known conduction problems (e.', NULL, 'Caution is advised if lacosamide is used concomitantly with other agents that prolong the PR interval, especially in the elderly and patients with known conduction problems (e.g., marked first-degree AV block, second-degree or higher AV block and sick sinus syndrome without pacemaker) or severe cardiac disease such as myocardial ischemia or heart failure. An ECG should be obtained in these patients before initiating lacosamide and after titration to steady-state.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57897/', '[1] "Product Information. Vimpat (lacosamide)." UCB Pharma Inc, Smyrna, GA.', 1767365204);
