INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (57876, 'Diltiazem', 'Midazolam', 'Moderate', 'Diltiazem and verapamil both have been reported to increase peak plasma concentration and to prolong the elimination half-life of midazolam. The mechanism probably is related to an increase in the bioavailability and a decrease in the plasma clearance of midazolam. Diltiazem, verapamil, midazolam, and triazolam all appear to be metabolized by the CYP450 (3A4) enzyme.', NULL, 'If diltiazem or verapamil must be used with midazolam or triazolam, close observation for increased and prolonged midazolam or triazolam effect is recommended.', NULL, 'If diltiazem or verapamil must be used with midazolam or triazolam, close observation for increased and prolonged midazolam or triazolam effect is recommended. Some investigators have suggested that midazolam dosage be reduced by at least 50% in patients who are receiving diltiazem or verapamil. Other investigators have suggested that triazolam be avoided in patients receiving diltiazem.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57952/', '[1] Kosuge K, Nishimoto M, Kimura M, Umemura K, Nakashima M, Ohashi K "Enhanced effect of triazolam with diltiazem." Br J Clin Pharmacol 43 (1997): 367-72[2] Varhe A, Olkkola KT, Neuvonen PJ "Diltiazem enhances the effects of triazolam by inhibiting its metabolism." Clin Pharmacol Ther 59 (1996): 369-75[3] Backman JT, Olkkola KT, Aranko K, Himberg JJ, Neuvonen PJ "Dose of midazolam should be reduced during diltiazem and verapamil treatments." Br J Clin Pharmacol 37 (1994): 221-5[4] Bailey DG, Dresser GK "Natural products and adverse drug interactions." Can Med A