INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (57911, 'Diltiazem', 'Nortriptyline', 'Moderate', 'Limited data suggest that diltiazem can increase the plasma concentrations of cyclic antidepressants. Data are available for nortriptyline, trimipramine, and imipramine. The proposed mechanism is inhibition of first-pass metabolism by diltiazem. ECG changes (first and second degree heart block) have been reported.', NULL, 'Close monitoring for clinical response and tolerance is recommended whenever diltiazem is added to or discontinued from an antidepressant regimen.', NULL, 'Close monitoring for clinical response and tolerance is recommended whenever diltiazem is added to or discontinued from an antidepressant regimen. Patients should be advised to notify their physician if they experience excessive antidepressant effects such as dry mouth, blurry vision, irregular or fast heartbeat, constipation, urinary retention, dizziness, or orthostatic hypotension. Dose adjustments may be necessary.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57987/', '[1] Krahenbuhl S, Smithgamble V, Hoppel CL "Pharmacokinetic interaction between diltiazem and nortriptyline." Eur J Clin Pharmacol 49 (1996): 417-9[2] Hermann DJ, Krol TF, Dukes GE, et al "Comparison of verapamil, diltiazem, and labetalol on the bioavailability and metabolism of imipramine." J Clin Pharmacol 32 (1992): 176-83[3] Hermann DJ, Krol TF, Dukes GE, et al "Comparison of verapamil, diltiazem, and labetalol on the bioavailability and metabolism of imipramine." J Clin Pharmacol 32 (1992): 176-83[4] Sigusch H, Henschel L, Kraul H, Merkel U, Hoffmann A "Lack of effect of grapefruit juice on d