INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (57946, 'Diltiazem', 'Pimavanserin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of pimavanserin, which is primarily metabolized by the isoenzyme. High plasma levels of pimavanserin may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is advised when pimavanserin is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when pimavanserin is used with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as peripheral edema and QT prolongation, and the pimavanserin dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58022/', '[1] "Product Information. Nuplazid (pimavanserin)." Accelis Pharma, East Windsor, NJ.[2] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (57947, 'Diltiazem', 'Pimecrolimus (topical)', 'Minor', 'Theoretically, coadministration with CYP450 3A inhibitors may increase the plasma concentrations of pimecrolimus, which has been shown in vitro to undergo metabolism by this subfamily of hepatic microsomal enzymes. Although clinically significant drug interactions are not expected due to minimal systemic absorption of topically administered pimecrolimus, the possibility cannot be ruled out.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58023/', '[1] "Product Information. Elidel (pimecrolimus topical)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (57948, 'Diltiazem', 'Pimozide', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of pimozide, which is partially metabolized by the isoenzyme. The use of pimozide has been associated with dose-related prolongation of the QT interval, thus elevated plasma levels of the drug may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of pimozide, concomitant use with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors is considered contraindicated.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of pimozide, concomitant use with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors is considered contraindicated. Some authorities consider concomitant administration of lurasidone and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole. With respect to less potent CYP450 3A4 inhibitors, the manufacturers recommend that they also not be used with pimozide.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58024/', '[1] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[2] Glassman AH, Bigger JT Jr "Antipsychotic drugs: prolonged QTc interval, torsade de pointes, and sudden death." Am J Psychiatry 158 (2001): 1774-82[3] Flockhart DA, Drici MD, Kerbusch T, et al "Studies on the mechanism of a fatal clarithromycin-pimozide interaction in a patient with tourette syndrome." J Clin Psychopharmacol 20 (2000): 317-24[4] Mangum EM, Graham KK "Lopinavir-Ritonavir: a new protease inhibitor." Pharmacotherapy 21 (2001): 1352-63[5] Desta Z, Kerbusch T, Flockhart DA "Effect of clarithromycin on the pharmacokinetics and pharmacodynamics of pimozide in healthy poor and extensive metabolizers of cytochrome P450 2D6 (CYP2D6)." Clin Pharmacol Ther 65 (1999): 10-20[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] "Product Information. Orap Tablets (pimozide)." Gate Pharmaceuticals, Sellersville, PA.[8] Dresser GK, Spence JD, Bailey DG "Pharmacokinetic-pharmacodynamic consequences and clinical relevance of cytochrome P450 3A4 inhibition." Clin Pharmacokinet 38 (2000): 41-57[9] Krahenbuhl S, Sauter B, Kupferschmidt H, Krause M, Wyss PA, Meier PJ "Case report: reversible QT prolongation with torsades de pointes in a patient with pimozide intoxication." Am J Med Sci 309 (1995): 315-6[10] Dresser GK, Spence JD, Bailey DG "Pharmacokinetic-pharmacodynamic consequences and clinical relevance of cytochrome P450 3A4 inhibition." Clin Pharmacokinet 38 (2000): 41-57[11] "Product Information. Orap Tablets (pimozide)." Gate Pharmaceuticals, Sellersville, PA.', 1767365204);
