INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (58066, 'Diltiazem', 'Tizanidine', 'Major', 'Tizanidine may potentiate the hypotensive effect of some medications secondary to its alpha-2 adrenergic activity. Pharmacologic studies have found tizanidine to possess between 1/10 to 1/50 of the potency of clonidine, a structurally similar agent, in lowering blood pressure.', NULL, 'A lower initial dosage and cautious dosage titration should be considered when tizanidine is initiated in patients receiving hypotensive medications.', NULL, 'A lower initial dosage and cautious dosage titration should be considered when tizanidine is initiated in patients receiving hypotensive medications. Although single doses of less than 8 mg of tizanidine have not been shown effective for spasticity in controlled clinical studies, it may be prudent to initiate treatment with 4 mg doses and gradually increase in 2 to 4 mg increments until optimum effect is achieved. The dose can be repeated at 6 to 8 hour intervals as needed, up to a maximum of three doses in 24 hours and a total daily dosage of 36 mg. However, experience with single doses exceeding 8 mg and daily doses exceeding 24 mg is limited. Close monitoring for development of hypotension is recommended.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58142/', '[1] "Product Information. Zanaflex (tizanidine)." Acorda Therapeutics, Hawthorne, NY.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (58067, 'Diltiazem', 'Tofacitinib', 'Moderate', 'Coadministration with one or more medications that may result in both moderate inhibition of CYP450 3A4 and potent inhibition of CYP450 2C19 may significantly increase the plasma concentrations of tofacitinib, which is primarily metabolized by the former isoenzyme with minor contribution from the latter. Side effects including lymphopenia, neutropenia, anemia, serious infections, and hyperlipidemia may be increased.', NULL, 'The dosage of tofacitinib should be reduced by 50% when used concomitantly with one or more medications that may result in both moderate inhibition of CYP450 3A4 and potent inhibition of CYP450 2C19.', NULL, 'The dosage of tofacitinib should be reduced by 50% when used concomitantly with one or more medications that may result in both moderate inhibition of CYP450 3A4 and potent inhibition of CYP450 2C19. For example, the dose for patients receiving 10 mg twice daily should be reduced to 5 mg twice daily and the dose for patients receiving 5 mg twice daily should be reduced to 5 mg once daily. For patients receiving 11 mg once daily of the extended-release formulation, the dose should be reduced to 5 mg once daily of the immediate-release formulation. The dose for patients receiving 3.2 mg twice daily should be reduced to 3.2 mg once daily and the dose for patients receiving 4 mg twice daily should be reduced to 4 mg once daily. Moderate inhibitors of CYP450 3A4 include amiodarone, aprepitant, ciprofloxacin, crizotinib, darunavir, dalfopristin-quinupristin, diltiazem, dronedarone, erythromycin, fluconazole, fusidic acid, grapefruit juice, imatinib, isavuconazonium, netupitant, and verapamil. Potent inhibitors of CYP450 2C19 include fluconazole, fluvoxamine, esomeprazole, lansoprazole, and omeprazole. Inhibitors of CYP450 2C19 alone are unlikely to substantially alter the pharmacokinetics of tofacitinib.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58143/', '[1] "Product Information. Xeljanz (tofacitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365204);
