INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (58361, 'Deferasirox', 'Cariprazine', 'Moderate', 'Coadministration with inducers of CYP450 3A4 is expected to alter the pharmacokinetics of cariprazine and its major active metabolites. Cariprazine is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2D6 to desmethyl cariprazine (DCAR) and didesmethyl cariprazine (DDCAR). DCAR is further metabolized to DDCAR by CYP450 3A4 and 2D6, and DDCAR is then metabolized by CYP450 3A4 to a hydroxylated metabolite. Both DCAR and DDCAR have in vitro receptor binding profiles similar to the parent drug and are considered pharmacologically equipotent to cariprazine.', NULL, 'Concomitant use of cariprazine with CYP450 3A4 inducers has not been evaluated and is not recommended.', NULL, 'Concomitant use of cariprazine with CYP450 3A4 inducers has not been evaluated and is not recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58437/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Vraylar (cariprazine)." Actavis Pharma, Inc., Parsippany, NJ.[3] "Product Information. Vraylar (cariprazine)." Actavis Pharma, Inc., Parsippany, NJ.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (58362, 'Delavirdine', 'Cariprazine', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of cariprazine and its major active metabolite, didesmethyl cariprazine (DDCAR), both of which are primarily metabolized by the isoenzyme.', NULL, 'The dosage of cariprazine should be reduced in the presence of a potent CYP450 3A4 inhibitor.', NULL, 'The dosage of cariprazine should be reduced in the presence of a potent CYP450 3A4 inhibitor. When initiating a potent CYP450 3A4 inhibitor during stable treatment with cariprazine, the manufacturer recommends reducing the current dosage of cariprazine by one-half. For patients taking 4.5 mg daily, the dosage should be reduced to 1.5 mg or 3 mg daily. For patients taking 1.5 mg daily, the dosing interval should be adjusted to every other day. Conversely, if cariprazine therapy is initiated during treatment with a potent CYP450 3A4 inhibitor, the recommended starting dosage of cariprazine is 1.5 mg on day 1 and day 3, with no dose administered on day 2. From day 4 onward, the dosage should be 1.5 mg daily, then increased to a maximum dosage of 3 mg daily. Following withdrawal of the CYP450 3A4 inhibitor, the dosage of cariprazine may need to be increased.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58438/', '[1] "Product Information. Vraylar (cariprazine)." Actavis Pharma, Inc., Parsippany, NJ.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Prod