INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (58401, 'Eslicarbazepine', 'Cariprazine', 'Moderate', 'Coadministration with potent or moderate inducers of CYP450 3A4 is expected to alter the pharmacokinetics of cariprazine and its major active metabolites. Cariprazine is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2D6 to desmethyl cariprazine (DCAR) and didesmethyl cariprazine (DDCAR). DCAR is further metabolized to DDCAR by CYP450 3A4 and 2D6, and DDCAR is then metabolized by CYP450 3A4 to a hydroxylated metabolite. Both DCAR and DDCAR have in vitro receptor binding profiles similar to the parent drug and are considered pharmacologically equipotent to cariprazine.', NULL, 'Concomitant use of cariprazine with potent or moderate CYP450 3A4 inducers has not been evaluated and is not recommended.', NULL, 'Concomitant use of cariprazine with potent or moderate CYP450 3A4 inducers has not been evaluated and is not recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58477/', '[1] "Product Information. Vraylar (cariprazine)." Actavis Pharma, Inc., Parsippany, NJ.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[3] "Product Information. Vraylar (cariprazine)." Actavis Pharma, Inc., Parsippany, NJ.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (58402, 'Esmolol', 'Cariprazine', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58478/', '[1] "Product Information. Seroquel (quetiapine)." Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[3] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Risperdal (risperidone)." Janssen Pharmaceutica, Titusville, NJ.[5] "Product Information. Abilify (aripiprazole)." Bristol-Myers Squibb, Princeton, NJ.[6] Markowitz JS, Wells BG, Carson WH "Interactions between antipsychotic and antihypertensive drugs." Ann Pharmacother 29 (1995): 603-9[7] Aronowitz JS, Chakos MH, Safferman AZ, Lieberman JA "Syncope associated with the combination of clozapine and enalapril." J Clin Psychopharmacol 14 (1994): 429-30[8] "Product Information. Zyprexa (olanzapine)." Lilly, Eli and Company, Indianapolis, IN.[9] Fruncillo R, Gibbons W, Vlasses P, Ferguson R "Severe hypotension associated with concurrent clonidine and antipsychotic medication." Am J Psychiatry 142 (1985): 274[10] "Product Information. Geodon (ziprasidone)." Pfizer US Pharmaceuticals, New York, NY.[11] White WB "Hypotension with postural syncope secondary to the combination of chlorpromazine and captopril." Arch Intern Med 146 (1986): 1833-4', 1767365204);
