INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (58485, 'Cariprazine', 'Lefamulin', 'Moderate', 'When administered orally, lefamulin may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. Based on interaction with midazolam, a sensitive CYP450 3A4 substrate, lefamulin may be a moderate CYP450 3A4 inhibitor. When oral midazolam was administered concomitantly with and at 2 or 4 hours after administration of lefamulin tablets, mean midazolam peak plasma concentration (Cmax) and systemic exposure (AUC) increased by approximately 100% and 200%, respectively.', NULL, 'Caution is advised when oral lefamulin is used with drugs that undergo metabolism via CYP450 3A4, particularly sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.', NULL, 'Caution is advised when oral lefamulin is used with drugs that undergo metabolism via CYP450 3A4, particularly sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.g., ergot alkaloids, colchicine, fentanyl, macrolide immunosuppressants, midazolam, triazolam, vardenafil, vinca alkaloids). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever oral lefamulin is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58561/', '[1] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc., King of Prussia, PA.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[6] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc., King of Prussia, PA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (58486, 'Cariprazine', 'Lemborexant', 'Moderate', 'Coadministration with inducers of CYP450 3A4 is expected to alter the pharmacokinetics of cariprazine and its major active metabolites. Cariprazine is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2D6 to desmethyl cariprazine (DCAR) and didesmethyl cariprazine (DDCAR). DCAR is further metabolized to DDCAR by CYP450 3A4 and 2D6, and DDCAR is then metabolized by CYP450 3A4 to a hydroxylated metabolite. Both DCAR and DDCAR have in vitro receptor binding profiles similar to the parent drug and are considered pharmacologically equipotent to cariprazine.', NULL, 'Concomitant use of cariprazine with CYP450 3A4 inducers has not been evaluated and is not recommended.', NULL, 'Concomitant use of cariprazine with CYP450 3A4 inducers has not been evaluated and is not recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58562/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Vraylar (cariprazine)." Actavis Pharma, Inc., Parsippany, NJ.[3] "Product Information. Vraylar (cariprazine)." Actavis Pharma, Inc., Parsippany, NJ.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365204);
