INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358989, 'Tubocurarine', 'Atenolol', 'Moderate', 'Neuromuscular blocking agents and beta-blockers may have additive cardiovascular depressant effects. Limited data suggest that beta-blockers may modestly potentiate the pharmacologic effects of neuromuscular blocking agents. The exact mechanism of interaction is unknown but may involve effects in the postsynaptic membrane, as beta-blockers alone have been reported to exacerbate or unmask myasthenia gravis.', 'DDInter', 1767317475, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers. Respiratory and cardiovascular status should be closely monitored.', 'Synergism', 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358990, 'Tyropanoic acid', 'Atenolol', 'Moderate', 'Limited data suggest that patients receiving beta-blockers may have an increased risk of severe hypotensive and/or hypersensitivity reactions to parenteral iodinated contrast media. In addition, allergic/anaphylactoid reactions in these patients may be more difficult to treat.', 'DDInter', 1767317475, 'Patients who have received beta-blockers should be closely monitored for adverse reactions to iodinated contrast media. If anaphylaxis occurs, clinicians should be aware that beta-blockers may attenuate the response to epinephrine. Thus, larger doses of epinephrine may be necessary to overcome the bronchospasm, although such large doses can also cause excessive alpha-adrenergic stimulation resulting in hypertension, reflex bradycardia, heart block, and possible potentiation of bronchospasm. Alternative treatments recommended include vigorous supportive care (e.g., fluids) and the use of parenteral beta-agonists for bronchospasm and norepinephrine for hypotension.', 'Synergism', 'Patients who have received beta-blockers should be closely monitored for adverse reactions to iodinated contrast media.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358991, 'Valsartan', 'Atenolol', 'Moderate', 'In the Valsartan Heart Failure Trial, the combination of valsartan with a beta-blocker and an ACE inhibitor was associated with unfavorable outcomes on morbidity and mortality in heart failure patients. The mechanism is unknown.', 'DDInter', 1767317475, 'The manufacturer recommends that the triple combination of valsartan with a beta-blocker and an ACE inhibitor be avoided in heart failure patients.', 'Others', 'The manufacturer recommends that the triple combination of valsartan with a beta-blocker and an ACE inhibitor be avoided in heart failure patients.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358992, 'Vardenafil', 'Atenolol', 'Moderate', 'Phosphodiesterase-5 (PDE5) inhibitors may potentiate the blood pressure-lowering effect of antihypertensive medications. These agents inhibit PDE5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can lead to peripheral vasodilation.', 'DDInter', 1767317475, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents. Patients receiving the combination should be advised to avoid rising abruptly from a sitting or recumbent position, especially following treatment initiation or a dosage increase, and to contact their doctor if they experience symptoms of hypotension such as dizziness, lightheadedness, fainting, or tachycardia.', 'Synergism', 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358993, 'Vecuronium', 'Atenolol', 'Moderate', 'Neuromuscular blocking agents and beta-blockers may have additive cardiovascular depressant effects. Limited data suggest that beta-blockers may modestly potentiate the pharmacologic effects of neuromuscular blocking agents. The exact mechanism of interaction is unknown but may involve effects in the postsynaptic membrane, as beta-blockers alone have been reported to exacerbate or unmask myasthenia gravis.', 'DDInter', 1767317475, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers. Respiratory and cardiovascular status should be closely monitored.', 'Synergism', 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358994, 'Verapamil', 'Atenolol', 'Major', 'Additive reductions in heart rate, cardiac conduction, and cardiac contractility may occur when calcium channel blockers, especially verapamil and diltiazem, are used concomitantly with beta blockers. While this combination may be useful and effective in some situations, potentially serious cardiovascular adverse effects such as congestive heart failure, severe hypotension, and/or exacerbation of angina may occur. Ventricular asystole, sinus arrest, and heart block have also been reported.', 'DDInter', 1767317475, 'Close clinical monitoring of patient hemodynamic response and tolerance is recommended if these agents are used together, and the dosage of one or both agents adjusted as necessary. Patients should be advised to promptly report any symptoms including fatigue, headache, fainting, swelling of the extremities, weight gain, shortness of breath, chest pain, increased or decreased heartbeat, or irregular heartbeat.', 'Synergism', 'Close clinical monitoring of patient hemodynamic response and tolerance is recommended if these agents are used together, and the dosage of one or both agents adjusted as necessary.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358995, 'Zaleplon', 'Atenolol', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317475, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358996, 'Ziprasidone', 'Atenolol', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', 'DDInter', 1767317475, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358997, 'Zolmitriptan', 'Atenolol', 'Minor', 'After one week of dosing with zolmitriptan and propranolol 160 mg/day, the Cmax and AUC of zolmitriptan increased 1.5 times while the Cmax and AUC of the N-desmethyl metabolite decreased by 30% and 15%, respectively. The mechanism of the interaction has not been described. It does not appear that any special precautions are necessary when coadministering zolmitriptan with beta-blockers, although further post-marketing studies will help to define this interaction.', 'DDInter', 1767317475, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358998, 'Zolpidem', 'Atenolol', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317475, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358999, 'Aminolevulinic acid', 'Chlorthalidone', 'Major', 'Patients exposed to photosensitizing agents may be at increased risk of a phototoxic skin reaction (severe sunburn) during use of aminolevulinic acid.', 'DDInter', 1767317475, 'Photosensitizing agents (e.g., fluoroquinolones, phenothiazines, retinoids, sulfonamides, sulfonylureas, tetracyclines, thiazide diuretics, griseofulvin, St. John''s wort) and topical preparations containing aminolevulinic acid should generally not be administered for 24 hours before and after oral administration of aminolevulinic acid.', 'Synergism', 'Photosensitizing agents (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1359000, 'Chlorthalidone', 'Captopril', 'Moderate', 'Although they are frequently combined in clinical practice, diuretics and angiotensin converting enzyme (ACE) inhibitors may have additive effects. Coadministration makes hypotension and hypovolemia more likely than does either drug alone. Some ACE inhibitors may attenuate the increase in the urinary excretion of sodium caused by some loop diuretics. Some patients on diuretics, especially those on dialysis or a dietary salt restriction, may experience acute hypotension with lightheadedness and dizziness after receiving the first dose of the ACE inhibitor. In addition, ACE inhibitors may cause renal insufficiency or acute renal failure in patients with sodium depletion or renal artery stenosis.', 'DDInter', 1767317475, 'Monitoring of blood pressure, diuresis, electrolytes, and renal function is recommended during coadministration. The possibility of first-dose hypotensive effects may be minimized by initiating therapy with small doses of the ACE inhibitor, or either discontinuing the diuretic temporarily or increasing the salt intake approximately one week prior to initiating an ACE inhibitor. Alternatively, the patient may remain under medical supervision for at least two hours after the first dose of the ACE inhibitor, or until blood pressure has stabilized.', 'Synergism', 'Monitoring of blood pressure, diuresis, electrolytes, and renal function is recommended during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1359001, 'Chlorthalidone', 'Carbamazepine', 'Moderate', 'Carbamazepine may cause clinically significant hyponatremia. Data show that the risk of hyponatremia may be additive when carbamazepine and hydrochlorothiazide are used concurrently. Carbamazepine and other diuretics may interact in a similar manner.', 'DDInter', 1767317475, 'Clinical monitoring of patient mental status and periodic measurement of serum sodium concentrations is recommended. Patients should be advised to notify their caregiver if they experience possible symptoms of hyponatremia such as nausea, vomiting, lethargy, weakness, muscle cramps, twitching, confusion, or seizures.', 'Synergism', 'Clinical monitoring of patient mental status and periodic measurement of serum sodium concentrations is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1359002, 'Cisapride', 'Chlorthalidone', 'Major', 'Some diuretics may produce hypokalemia and hypomagnesemia which could potentiate the risk of cisapride-induced arrhythmias in patients receiving both drugs concomitantly. Prolonged QT interval, torsades de pointes, and death have been reported.', 'DDInter', 1767317475, 'Cisapride is contraindicated in patients with uncorrected hypokalemia and hypomagnesemia, such as those taking potassium-wasting diuretics.', 'Synergism', 'Cisapride is contraindicated in patients with uncorrected hypokalemia and hypomagnesemia, such as those taking potassium-wasting diuretics.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1359003, 'Cyclophosphamide', 'Chlorthalidone', 'Moderate', 'Antineoplastic-induced bone marrow suppression may be prolonged with concomitant thiazide administration. The mechanism is unknown. Thiazides have been associated with blood dyscrasias.', 'DDInter', 1767317475, 'Alternative antihypertensive therapy may be advisable. Hematological status should be closely monitored in patients receiving this combination.', 'Others', 'Alternative antihypertensive therapy may be advisable.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1359004, 'Cyclosporine', 'Chlorthalidone', 'Moderate', 'Coadministration of thiazide or thiazide-like diuretics with cyclosporine may potentially result in hypermagnesemia, hyperuricemia, and increase the risk of nephrotoxicity.', 'DDInter', 1767317475, 'Monitoring of renal function, serum electrolytes, uric acid levels, and cyclosporine blood concentrations is advisable during concomitant use. Some authorities advise against concomitant use of these medicines.', 'Others', 'Monitoring of renal function, serum electrolytes, uric acid levels, and cyclosporine blood concentrations is advisable during concomitant use.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1359005, 'Diazoxide', 'Chlorthalidone', 'Moderate', 'The combination of thiazide diuretics and diazoxide may result in profound and prolonged hyperglycemia. The mechanism may be related to additive hyperglycemic effects.', 'DDInter', 1767317475, 'Blood glucose monitoring is recommended during coadministration.', 'Synergism', 'Blood glucose monitoring is recommended during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1359006, 'Dofetilide', 'Chlorthalidone', 'Major', 'Dofetilide can cause dose- and concentration-related QT interval prolongation. Theoretically, coadministration with agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes, because of additive arrhythmogenic potential.', 'DDInter', 1767317475, 'Caution is advised if dofetilide must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with dofetilide. During therapy, potassium concentrations should be kept above 4 mEq/dL and magnesium concentrations above 1.8 mg/dL. Patients should also have frequent ECGs and be monitored for serious arrhythmias when QT intervals are prolonged. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Synergism', 'Caution is advised if dofetilide must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1359007, 'Dolasetron', 'Chlorthalidone', 'Major', 'The risk of arrhythmia may be increased in patients taking dolasetron and diuretics. Dolasetron induced ECG changes (PR and QTc prolongation; QRS widening) have been observed in healthy volunteers and in controlled clinical trials. Diuretics may further predispose the patient to arrhythmia by inducing electrolyte abnormalities.', 'DDInter', 1767317475, 'Close clinical and laboratory monitoring for cardiac rhythm and electrolyte disturbances is recommended if this combination is used.', 'Synergism', 'Close clinical and laboratory monitoring for cardiac rhythm and electrolyte disturbances is recommended if this combination is used.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1359008, 'Dronedarone', 'Chlorthalidone', 'Major', 'Dronedarone may cause dose-related prolongation of the QT interval. Since hypokalemia and hypomagnesemia are known risk factors for arrhythmia associated with QT prolongation, coadministration of dronedarone with agents that can cause significant potassium and/or magnesium loss (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may increase the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 1767317475, 'Caution is advised if dronedarone must be used concomitantly with medications that can cause significant potassium and/or magnesium loss. Serum electrolytes should be evaluated at baseline and at regular intervals throughout the duration of therapy. Any abnormalities should be corrected prior to initiating therapy, and levels should be maintained within the normal range during treatment. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope. Dronedarone should be discontinued if QTc Bazett interval increases to 500 ms or greater.', 'Synergism', 'Caution is advised if dronedarone must be used concomitantly with medications that can cause significant potassium and/or magnesium loss.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1359009, 'Droperidol', 'Chlorthalidone', 'Major', 'The use of droperidol has been associated with QT interval prolongation, torsade de pointes and other serious arrhythmias, and sudden death. The concurrent administration of agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins), drugs known to increase the QT interval (e.g., phenothiazines, tricyclic antidepressants, antiarrhythmic agents, etc.), certain other drugs (benzodiazepines, volatile anesthetics, intravenous opiates), or alcohol abuse may increase the risk of prolonged QT syndrome.', 'DDInter', 1767317475, 'The manufacturer recommends extreme caution if droperidol must be given concomitantly with these agents. The dosage of droperidol should be individualized and titrated to the desired effect. Routine vital sign and ECG monitoring is recommended. When droperidol is used in combination with other drugs that cause CNS and/or respiratory depression, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Synergism', 'The manufacturer recommends extreme caution if droperidol must be given concomitantly with these agents.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1359010, 'Enalapril', 'Chlorthalidone', 'Moderate', 'Although they are frequently combined in clinical practice, diuretics and angiotensin converting enzyme (ACE) inhibitors may have additive effects. Coadministration makes hypotension and hypovolemia more likely than does either drug alone. Some ACE inhibitors may attenuate the increase in the urinary excretion of sodium caused by some loop diuretics. Some patients on diuretics, especially those on dialysis or a dietary salt restriction, may experience acute hypotension with lightheadedness and dizziness after receiving the first dose of the ACE inhibitor. In addition, ACE inhibitors may cause renal insufficiency or acute renal failure in patients with sodium depletion or renal artery stenosis.', 'DDInter', 1767317475, 'Monitoring of blood pressure, diuresis, electrolytes, and renal function is recommended during coadministration. The possibility of first-dose hypotensive effects may be minimized by initiating therapy with small doses of the ACE inhibitor, or either discontinuing the diuretic temporarily or increasing the salt intake approximately one week prior to initiating an ACE inhibitor. Alternatively, the patient may remain under medical supervision for at least two hours after the first dose of the ACE inhibitor, or until blood pressure has stabilized.', 'Synergism', 'Monitoring of blood pressure, diuresis, electrolytes, and renal function is recommended during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1359011, 'Guanabenz', 'Chlorthalidone', 'Moderate', 'The hypotensive effects of thiazide diuretics and alpha-adrenergic blockers may be additive. Postural hypotension may occur.', 'DDInter', 1767317475, 'Hemodynamic responses should be monitored during coadministration, especially during the first few weeks of therapy. Patients should be advised to take the alpha-blocker at bedtime and to notify their physician if they experience dizziness or syncope while awake.', 'Synergism', 'Hemodynamic responses should be monitored during coadministration, especially during the first few weeks of therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1359012, 'Chlorthalidone', 'Labetalol', 'Moderate', 'Although they are often combined in clinical practice, diuretics and beta-blockers may increase the risk of hyperglycemia and hypertriglyceridemia in some patients, especially in patients with diabetes or latent diabetes. In addition, the risk of QT interval prolongation and arrhythmias (e.g. torsades de pointes) due to sotalol may be increased by potassium-depleting diuretics.', 'DDInter', 1767317475, 'Monitoring of serum potassium levels, blood pressure, and blood glucose is recommended during coadministration. Patients should be advised to seek medical assistance if they experience dizziness, weakness, fainting, fast or irregular heartbeats, or loss of blood glucose control.', 'Synergism', 'Monitoring of serum potassium levels, blood pressure, and blood glucose is recommended during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1359013, 'Chlorthalidone', 'Levacetylmethadol', 'Major', 'Levomethadyl acetate can cause QT interval prolongation and torsades de pointes. Coadministration with agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in an elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes.', 'DDInter', 1767317475, 'Caution is advised if levomethadyl acetate must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with levomethadyl acetate. A 12-lead ECG should be performed before initiating therapy, 12 to 14 days after initiating therapy, and periodically thereafter. Patients should be advised to immediately seek medical attention if they experience palpitations, dizziness, lightheadedness, fainting, or seizures.', 'Synergism', 'Caution is advised if levomethadyl acetate must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1359014, 'Chlorthalidone', 'Lithium carbonate', 'Major', 'Thiazide diuretics may cause a rapid increase in serum lithium levels and potentiate the risk of lithium toxicity. The exact mechanism is unknown but may be related to the sodium loss induced by thiazide diuresis, which produces a compensatory increase in proximal tubular reabsorption of sodium along with lithium.', 'DDInter', 1767317475, 'Thiazide diuretics should generally not be prescribed to patients receiving lithium unless close monitoring of serum lithium levels and electrolytes can be rendered. Lithium dose reductions may be required. Patients should be advised to notify their physician if they experience potential signs and symptoms of lithium toxicity such as drowsiness, dizziness, muscle weakness, vomiting, diarrhea, thirst, polyuria, tinnitus, tremor, ataxia, and blurred vision.', 'Excretion', 'Thiazide diuretics should generally not be prescribed to patients receiving lithium unless close monitoring of serum lithium levels and electrolytes can be rendered.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1359015, 'Chlorthalidone', 'Methenamine', 'Moderate', 'Agents that can alkalinize the urine such as thiazide diuretics, carbonic anhydrase inhibitors, and antacids may decrease the antibacterial effectiveness of methenamine by inhibiting its conversion to formaldehyde. Methenamine is most effectively converted in an acidic milieu of pH less than 5.5.', 'DDInter', 1767317475, 'Concomitant use of methenamine-containing preparations with thiazide diuretics, carbonic anhydrase inhibitors, or large doses of antacids should be avoided if possible. Otherwise, frequent urine pH testing may be considered. Some methenamine products may be used with antacids if dosing times are separated by at least one hour. Consult the manufacturer''s product labeling for specific recommendations.', 'Antagonism', 'Concomitant use of methenamine-containing preparations with thiazide diuretics, carbonic anhydrase inhibitors, or large doses of antacids should be avoided if possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1359016, 'Chlorthalidone', 'Methotrexate', 'Moderate', 'Antineoplastic-induced bone marrow suppression may be prolonged with concomitant thiazide administration. The mechanism is unknown. Thiazides have been associated with blood dyscrasias.', 'DDInter', 1767317475, 'Alternative antihypertensive therapy may be advisable. Hematological status should be closely monitored in patients receiving this combination.', 'Others', 'Alternative antihypertensive therapy may be advisable.', NULL, NULL, 'Moderate', 'pharmacodynamic');
