INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (58579, 'Cariprazine', 'Pazopanib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of cariprazine and its major active metabolite, didesmethyl cariprazine (DDCAR), both of which are primarily metabolized by the isoenzyme. The extent to which other, less potent inhibitors of CYP450 3A4 may interact with cariprazine and its metabolites is unknown.', NULL, 'Caution is advised when cariprazine is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when cariprazine is prescribed with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as extrapyramidal symptoms, cognitive and motor impairment, hyperglycemia, dyslipidemia, weight gain, orthostatic hypotension, leucopenia, neutropenia, seizures and dysphagia, and the dosage of cariprazine adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Brexpiprazole, Loxapine, Molindone, Thiothixene', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58655/', '[1] "Product Information. Vraylar (cariprazine)." Actavis Pharma, Inc., Parsippany, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.[4] "Product Information. Vraylar (cariprazine)." Actavis Pharma, Inc., Parsippany, NJ.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (58580, 'Cariprazine', 'Penbutolol', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Droperidol, Pimozide, Pimavanserin', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58656/', '[1] "Product Information. Seroquel (quetiapine)." Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[3] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Risperdal (risperidone)." Janssen Pharmaceutica, Titusville, NJ.[5] "Product Information. Abilify (aripiprazole)." Bristol-Myers Squibb, Princeton, NJ.[6] Markowitz JS, Wells BG, Carson WH "Interactions between antipsychotic and antihypertensive drugs." Ann Pharmacother 29 (1995): 603-9[7] Aronowitz JS, Chakos MH, Safferman AZ, Lieberman JA "Syncope associated with the combination of clozapine and enalapril." J Clin Psychopharmacol 14 (1994): 429-30[8] "Product Information. Zyprexa (olanzapine)." Lilly, Eli and Company, Indianapolis, IN.[9] Fruncillo R, Gibbons W, Vlasses P, Ferguson R "Severe hypotension associated with concurrent clonidine and antipsychotic medication." Am J Psychiatry 142 (1985): 274[10] "Product Information. Geodon (ziprasidone)." Pfizer US Pharmaceuticals, New York, NY.[11] White WB "Hypotension with postural syncope secondary to the combination of chlorpromazine and captopril." Arch Intern Med 146 (1986): 1833-4', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (58581, 'Cariprazine', 'Pentazocine', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', 'Naltrexone, Acetaminophen, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone', 'Amisulpride', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58657/', '[1] US Food and Drug Administration "FDA warns about serious risks and death when combining opioid pain or cough medicines with benzodiazepines; requires its strongest warning. Available from: URL: http://www.fda.gov/downloads/Drugs/DrugSafety/UCM518672.pdf." ([2016, Aug 31]):', 