INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59077, 'Carmustine', 'Peginterferon beta-1a', 'Moderate', 'Coadministration of beta interferons with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Use of beta interferons has been associated with rare cases of liver injury, including autoimmune hepatitis and severe liver damage leading to hepatic failure, some of which required transplantation.', NULL, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.', NULL, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; other interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59153/', '[1] "Product Information. Plegridy (peginterferon beta-1a)." Biogen Idec Inc, Cambridge, MA.[2] "Product Information. Betaseron (interferon beta-1b)." Berlex, Richmond, CA.[3] "Product Information. Rebif (interferon beta-1a)." Serono Laboratories Inc, Norwell, MA.[4] "Product Information. Avonex (interferon beta-1a)." Biogen, Cambridge, MA.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59078, 'Carmustine', 'Penbutolol', 'Minor', 'Some alkylating agents may significantly increase the free fraction of penbutolol by displacing it from plasma protein-binding sites (alpha-1 acid glycoprotein). Data are available for carmustine (BCNU) and mechlorethamine. The clinical significance is unknown. Clinicians should be aware of the interaction and observe patients for increased beta-blockade.', NULL, '-', NULL, '-', 'Distribution', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59154/', '[1] Aguirre C, Rodriguez-Sasiain JM, Calvo R "Decrease in penbutolol protein binding as a consequence of treatment with some alkylating agents." Cancer Chemother Pharmacol 34 (1994): 86-8', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59079, 'Carmustine', 'Pexidartinib', 'Major', 'Serious cases of hepatotoxicity, some fatal, have occurred in patients treated with pexidartinib. Concomitant use of other potentially hepatotoxic agents may potentiate the risk of liver injury. The mechanism of hepatotoxicity is unknown, its occurrence cannot be predicted, and it is unknown whether liver injury occurs in the absence of increased transaminases.', NULL, 'The use of pexidartinib with other potentially hepatotoxic agents should be avoided.', NULL, 'The use of pexidartinib with other potentially hepatotoxic agents should be avoided. Patients treated with pexidartinib should have liver function tests, including AST, ALT, total bilirubin, direct bilirubin, ALP, and gamma-glutamyl transferase (GGT), prior to initiation of pexidartinib, weekly for the first 8 weeks, every 2 weeks for the next month, and every 3 months thereafter. Pexidartinib therapy may require a dosage reduction, to be withheld, or permanently discontinued based on the severity of the hepatotoxicity. A recurrence of increased serum transaminases, bilirubin, or ALP may occur upon rechallenge with a reduced dose of pexidartinib. Liver function tests should be performed weekly for the first month after rechallenge. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59155/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59080, 'Carmustine', 'Phenobarbital', 'Moderate', 'Coadministration of carmustine with phenobarbital can increase the metabolism of carmustine, which may weaken its antitumor activity. The mechanism of interaction has not been established; however, it appears to involve induction of hepatic enzymes.', NULL, 'Pharmacologic response to carmustine should be monitored more closely whenever phenobarbital is added to or withdrawn from therapy, and the dosages adjusted as necessary.', NULL, 'Pharmacologic response to carmustine should be monitored more closely whenever phenobarbital is added to or withdrawn from therapy, and the dosages adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59156/', '[1] "Product Information. BiCNU (carmustine)." Bristol-Myers Squibb, Princeton, NJ.', 1767363021);
