INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59320, 'Spironolactone', 'Ranolazine', 'Moderate', 'Coadministration with inhibitors of the P-glycoprotein (P-gp) efflux transporter may increase the oral bioavailability of ranolazine, which has been shown in vitro to be a substrate of P-gp. Ranolazine can prolong the QT interval in a dose-dependent manner, thus increased plasma levels may potentiate the risk of ventricular arrhythmias including ventricular fibrillation and torsade de pointes.', NULL, 'Caution is advised if ranolazine is prescribed in combination with P-gp inhibitors.', NULL, 'Caution is advised if ranolazine is prescribed in combination with P-gp inhibitors. Pharmacologic response to ranolazine should be monitored more closely whenever a P-gp inhibitor is added to or withdrawn from therapy, and the ranolazine dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59396/', '[1] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.[2] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59321, 'Spironolactone', 'Rasagiline', 'Moderate', 'Monoamine oxidase inhibitors (MAOIs) may potentiate the hypotensive effect of some medications. MAOIs alone quite commonly produce orthostatic hypotension. This effect may stem from a gradual MAOI-induced accumulation of false neurotransmitters in peripheral adrenergic neurons that have minimal activity at alpha- and beta-adrenergic receptors, resulting in a functional block of sympathetic neurotransmission. The interaction has been reported with the concomitant use of beta-blockers.', NULL, 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment.', NULL, 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment. Close monitoring for development of hypotension is recommended. Ambulatory patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59397/', '[1] Reggev A, Vollhardt BR "Bradycardia induced by an interaction between phenelzine and beta blockers." Psychosomatics 30 (1992): 106-8[2] "Product Information. Marplan (isocarboxazid)" Roche Laboratories, Nutley, NJ.[3] Schulz R, Antonin KH, Hoffmann E, et al "Tyramine kinetics and pressor sensitivity during monoamine oxidase inhibition by selegiline." Clin Pharmacol Ther 46 (1989): 528-36[4] Ban TA "Drug i