INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59328, 'Spironolactone', 'Romidepsin', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of romidepsin, which is a substrate of the efflux transporter. Although there are no formal drug interaction studies, the interaction may increase the risk of hematologic toxicities such as anemia, leukopenia, and thrombocytopenia as well as electrocardiographic changes such as QT interval prolongation and T-wave and ST-segment changes.', NULL, 'Caution is advised if romidepsin is prescribed in combination with P-gp inhibitors.', NULL, 'Caution is advised if romidepsin is prescribed in combination with P-gp inhibitors. Pharmacologic response to romidepsin should be monitored more closely whenever a P-gp inhibitor is added to or withdrawn from therapy, and the romidepsin dosage adjusted as necessary. Patients should have complete blood cell counts, electrocardiograms, and serum electrolyte levels performed at baseline and regular intervals as recommended in the product labeling. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59404/', '[1] "Product Information. Istodax (romidepsin)." Gloucester Pharmaceuticals, Cambridge, MA.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59329, 'Spironolactone', 'Rucaparib', 'Moderate', 'Coadministration with potent inhibitors of P-glycoprotein (P-gp) may increase the plasma concentration and the risk of adverse effects of rucaparib, which has been shown to be a substrate of P-gp. However, the effects on the pharmacokinetics of rucaparib have not been evaluated in vivo.', NULL, 'Caution is advised if rucaparib is used concomitantly with potent P-gp inhibitors.', NULL, 'Caution is advised if rucaparib is used concomitantly with potent P-gp inhibitors. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever a potent P-gp inhibitor is added to or withdrawn from therapy with rucaparib.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59405/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):', 1767363021);
