INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59373, 'Vardenafil', 'Spironolactone', 'Moderate', 'Phosphodiesterase-5 (PDE5) inhibitors may potentiate the blood pressure-lowering effect of antihypertensive medications. These agents inhibit PDE5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can lead to peripheral vasodilation.', NULL, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents.', NULL, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents. Patients receiving the combination should be advised to avoid rising abruptly from a sitting or recumbent position, especially following treatment initiation or a dosage increase, and to contact their doctor if they experience symptoms of hypotension such as dizziness, lightheadedness, fainting, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59449/', '[1] "Product Information. Stendra (avanafil)." Vivus Inc, Mountain View, CA.[2] "Product Information. Viagra (sildenafil)." Pfizer US Pharmaceuticals, New York, NY.[3] "Product Information. Levitra (vardenafil)." Bayer, West Haven, CT.[4] Jetter A, Kinzig-Schippers M, Walchner-Bonjean M, et al. "Effects of grapefruit juice on the pharmacokinetics of sildenafil." Clin Pharmacol Ther 71 (2002): 21-29', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59374, 'Venetoclax', 'Spironolactone', 'Major', 'Coadministration with P-glycoprotein (P-gp) inhibitors may increase the plasma concentrations of venetoclax, which is a substrate of this efflux transporter.', NULL, 'Concomitant use of venetoclax with P-gp inhibitors should generally be avoided.', NULL, 'Concomitant use of venetoclax with P-gp inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends that venetoclax dosage be reduced by at least 50%. The dosage used prior to initiating the P-gp inhibitor may be resumed 2 to 3 days after discontinuation of the inhibitor.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59450/', '[1] "Product Information. Venclexta (venetoclax)." AbbVie US LLC, North Chicago, IL.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Venclexta (venetoclax)." AbbVie US LLC, North Chicago, IL.[5] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59375, 'Venlafaxine', 'Spironolactone', 'Moderate', 'Coadministration with diuretics may potentiate the risk of hyponatremia associated with the use of selective serotonin reuptake inhibitors (SSRIs) and serotonin-norepinephrine reuptake inhibitors (SNRIs). Elderly patients and patients taking diuretics or who are otherwise volume-depleted may be at greater risk of developing hyponatremia with SSRIs and SNRIs. Antihypertensive agents such as diuretics may potentiate the orthostatic effect that is occasionally observed upon the initiation of SSRI or SNRI therapy. Syncope and orthostatic hypotension tend to occur within the first week of SNRI/SSRI therapy but can occur at any time during treatment, particularly after a dosage increase. The use of SSRIs or SNRIs may also cause sustained increases in blood pressure and heart rate, which may antagonize the therapeutic effects of antihypertensive medications.', NULL, 'Caution is recommended if SSRIs or SNRIs are prescribed in combination with diuretics, particularly in the elderly.', NULL, 'Caution is recommended if SSRIs or SNRIs are prescribed in combination with diuretics, particularly in the elderly. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hyponatremia such as nausea, vomiting, headache, malaise, lethargy, irritability, difficulty concentrating, memory impairment, confusion, weakness, muscle spasm, and unsteadiness (which may lead to falls). More severe and/or acute cases may include hallucination, syncope, seizure, coma, respiratory arrest, and death. Discontinuation of SSRI/SNRI therapy should be considered in patients who develop symptomatic hyponatremia, and appropriate medical intervention instituted as necessary. Patients should also have their blood pressure and pulse monitored before and during SSRI/SNRI therapy, especially during the first few weeks and following a dosage increase. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59451/', '[1] "Product Information. Savella (milnacipran)." Forest Pharmaceuticals, St. Louis, MO.[2] Jackson C, Carson W, Markowitz J, Mintzer J "SIADH associated with fluoxetine and sertraline therapy." Am J Psychiatry 152 (1995): 809-10[3] Robinson D, Brooks J, Mahler E, Sheikh JI "SIADH--compulsive drinking or SSRI influence?" Ann Pharmacother 30 (1996): 885[4] Odeh M, Beny A, Oliven A "Severe symptomatic hyponatremia during citalopram therapy." Am J Med Sci 321 (2001): 159-60[5] Vishwanath BM, Navalgund AA, Cusano W, Navalgund KA "Fluoxetine as a cause of SIADH." Am J Psychiatry 148 (1991): 542-3[6] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990): 385-6[7] Jacob S, Spinler SA "Hyponatremia associated with selective serotonin-reuptake inhibitors in older adults." Ann Pharmacother 40 (2006): 1618-22[8] Fitzgerald MA "Hyponatremia associated with SSRI use in a 65-year-old woman." Nurse Pract 33 (2008): 11-2[9] van Campen JP, Voets AJ "SIADH caused by paroxetine." Ann Pharmacother 30 (1996): 1499[10] "Product Information. Effexor (venlafaxine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[11] Kessler J, Samuels SC "Sertraline and hyponatremia." N Engl J Med 335 (1996): 524[12] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991): 139[13] Pacher P, Kecskemeti V "Cardiovascular side effects of new anti