INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59553, 'Carvedilol', 'Berotralstat', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may significantly increase the plasma concentrations of berotralstat. Berotralstat is a substrate of both P-gp and BCRP.', NULL, 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with P-gp and/or BCRP inhibitors.', NULL, 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with P-gp and/or BCRP inhibitors. Patients should be advised to contact their physician if they experience undue adverse effects of berotralstat such as abdominal pain, vomiting, or diarrhea. Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59634/', '[1] "Product Information. Orladeyo (berotralstat)." BioCryst Pharmaceuticals Inc, Durham, NC.[2] Min DI, Ku YM, Geraets DR, Lee HC "Effect of grapefruit juice on the pharmacokinetics and pharmacodynamics of quinidine in healthy volunteers." J Clin Pharmacol 36 (1996): 469-76[3] Ace LN, Jaffe JM, Kunka RL "Effect of food and an antacid on quinidine bioavailability." Biopharm Drug Dispos 4 (1983): 183-90[4] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[5] Ha HR, Chen J, Leuenberger PM, Freiburghaus AU, Follah F "In vitro inhibition of midazolam and quinidine metabolism by flavonoids." Eur J Clin Pharmacol 48 (1995): 367-71', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59554, 'Carvedilol', 'Bromfenac', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effect of beta-blockers. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Indomethacin and piroxicam have been reported to have greater attenuating effects than other NSAIDs, and indomethacin effects may be significant in patients with eclampsia.', NULL, 'Patients receiving a beta-blocker who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, 'Patients receiving a beta-blocker who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59635/', '[1] Radack KL, Deck CC, Bloomfield SS "Ibuprofen interferes with the efficacy of antihypertensive drugs." Ann Intern Med 107 (1987): 628-35[2] Hartmann D, Stief G, Lingenfelder M, Guzelhan C, Horsch AK "Study on the possible interaction between tenoxicam and atenolol in hypertensive patients." Arzneimittelforschung 45-1 (1995): 494-8[3] Abate MA, Neely JL, Layne RD, D