INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59612, 'Docetaxel', 'Carvedilol', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the blood concentrations of the taxanes, docetaxel and paclitaxel, which have been shown to be substrates of the efflux transporter.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of paclitaxel or docetaxel therapy should be considered during coadministration with P-gp inhibitors, including adverse effects such as myelosuppression, stomatitis, neurotoxicity (e.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of paclitaxel or docetaxel therapy should be considered during coadministration with P-gp inhibitors, including adverse effects such as myelosuppression, stomatitis, neurotoxicity (e.g., paraesthesia, dysesthesia, pain), myalgia, asthenia, fluid retention, nausea, vomiting, and diarrhea. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a P-gp inhibitor is added to or withdrawn from therapy.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59693/', '[1] Aronson JK, Grahame-Smith DG "Clinical pharmacology: adverse drug interactions." Br Med J 282 (1981): 288-91[2] Vodovar D, Arnaout M, Mongardon N, et al. "Severe docetaxel overdose induced by pharmacokinetic interaction with dronedarone." J Clin Oncol 29 (2011): e694-5[3] Starr SP, Hammann F, Gotta V, et al. "Pharmacokinetic interaction between taxanes and amiodarone leading to severe toxicity." Br J Clin Pharmacol 450 (2016): 22-27[4] "Product Information. Taxotere (docetaxel)." Rhone-Poulenc Rorer, Collegeville, PA