INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59708, 'Isoprenaline', 'Carvedilol', 'Moderate', 'Some beta-blockers may antagonize the bronchodilatory, hypotensive, and tachycardic effects of isoproterenol. The mechanism is blockade of beta-adrenergic receptors, which leads to bronchoconstriction, vasodilation, and increased heart rate. Beta-blockers have been used successfully to treat catecholamine or isoproterenol-induced tachyarrhythmias.', NULL, 'This combination should generally be avoided.', NULL, 'This combination should generally be avoided. Patients who are receiving isoproterenol for cardiac conditions should be closely monitored for adequate therapeutic effect if a beta blocker is added. If no alternative exists, small doses of a B-1 selective beta-blocker (e.g., acebutolol, atenolol, betaxolol, bisoprolol, or metoprolol) may be preferable; however, respiratory status should be closely monitored in patients with obstructive pulmonary disease.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59790/', '[1] Ziegler MG, Chernow B, Woodson LC, Coyle J, Cruess D, Lake CR "The effect of propranolol on catecholamine clearance." Clin Pharmacol Ther 40 (1986): 116-9[2] Pickles H, Perucca E, Fish A, Richens A "Propranolol and sotalol as antagonists of isoproterenol-enhanced physiologic tremor." Clin Pharmacol Ther 30 (1981): 303-10[3] Johnsson G, Svedmyr N, Thiringer G "Effects of intravenous propranolol and metoprolol and their interaction with isoprenaline on pulmonary function, heart rate and blood pressure in asthmatics." Eur J Clin Pharmacol 8 (1975): 175-80[4] Thiringer G, Svedmyr N "Interaction of orally administered metoprolol, practolol and propranolol with isoprenaline in asthmatics." Eur J Clin Pharmacol 10 (1976): 163-70[5] Vlay SC "Isoproterenol-induced bradyarrhythmias." Am Heart J 122 (1991): 1169[6] Falliers CJ, Vincent ME, Medakovic M "Effect of single doses of labetalol, metoprolol, and placebo on ventilatory function in patients with bronchial asthma: interaction with isoproterenol." J Asthma 23 (1986): 251-60[7] Vlay SC "Catecholamine-sensitive ventricular tachycardia." Am Heart J 114 (1987): 455-61[8] Messerli FH, Kuchel O, Tolis G, Hamet P, Fraysse J, Genest J "Effects of beta-adrenergic blockade on plasma cyclic AMP and blood sugar responses to glucagon and isoproterenol in man." Int J Clin Pharmacol Biopharm 14 (1976): 189-94[9] Mann DE, Marmont P, Shultz J, Reiter MJ "Atrioventricular nodal reentrant tachycardia initiated by catecholamine-induced ventricular tachycardia. A case report." J Electrocardiol 24 (1991): 191-5', 1767365204);
