INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59713, 'Carvedilol', 'Lacosamide', 'Moderate', 'Lacosamide has been shown to prolong the PR interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, calcium channel blockers, digoxin, atazanavir, mefloquine) may result in elevated risk of conduction disturbances and atrioventricular block.', NULL, 'Caution is advised if lacosamide is used concomitantly with other agents that prolong the PR interval, especially in the elderly and patients with known conduction problems (e.', NULL, 'Caution is advised if lacosamide is used concomitantly with other agents that prolong the PR interval, especially in the elderly and patients with known conduction problems (e.g., marked first-degree AV block, second-degree or higher AV block and sick sinus syndrome without pacemaker) or severe cardiac disease such as myocardial ischemia or heart failure. An ECG should be obtained in these patients before initiating lacosamide and after titration to steady-state.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59795/', '[1] "Product Information. Vimpat (lacosamide)." UCB Pharma Inc, Smyrna, GA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59714, 'Carvedilol', 'Lanreotide', 'Moderate', 'Somatostatin analogs (e.g., octreotide, lanreotide) can decrease heart rate and may potentiate the bradycardic effects of drugs like beta-blockers, calcium channel blockers, digitalis glycosides, and certain antiarrhythmic agents (e.g., class I antiarrhythmics, adenosine, amiodarone).', NULL, 'Caution is advised if somatostatin analogs are prescribed with agents that exhibit bradycardic effects, including ophthalmic formulations of beta-blockers.', NULL, 'Caution is advised if somatostatin analogs are prescribed with agents that exhibit bradycardic effects, including ophthalmic formulations of beta-blockers. Heart rate and blood pressure should be monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59796/', '[1] "Product Information. Sandostatin (octreotide)." Sandoz Pharmaceuticals Corporation, East Hanover, NJ.[2] "Product Information. Somatuline Depot (lanreotide)." Ipsen Inc, Milford, MA.[3] "Product Information. Sandostatin (octreotide)." Sandoz Pharmaceuticals Corporation, East Hanover, NJ.[4] "Product Information. Somatuline Depot (lanreotide)." Ipsen Inc, Milford, MA.[5] Katz MD, Erstad BL "Octreotide, a new somatostatin analogue." Clin Pharm 8 (1989): 255-73[6] Ho PJ, Boyajy LD, Greenstein E, Barkan AL "Effect of chronic octreotide treatment on intestinal absorption in patients with acromegaly." Dig Dis Sci 38 (1993): 309-15[7] Landgraf R, Landgraf-Leurs MM, Nusser J, et al "Effect of somatostatin analogue (SMS201-995) on cyclosporine levels." Transplantation 44 (1987): 724-5', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59715, 'Carvedilol', 'Lanthanum carbonate', 'Moderate', 'INTERVAL: Theoretically, lanthanum carbonate may chelate with certain drugs in the gastrointestinal tract, resulting in reduced oral bioavailability of those drugs during coadministration.', NULL, 'To minimize the potential for interaction, drugs that are known to interact with antacids (e.', NULL, 'To minimize the potential for interaction, drugs that are known to interact with antacids (e.g., ACE inhibitors, beta blockers, bisphosphonates, coumarin derivatives, digitalis glycosides, fluoroquinolones, iron, phenytoin, rifampin, tetracyclines, thyroid preparations, valproic acid) should not be taken within 2 hours of administration of lanthanum carbonate according to the product labeling.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59797/', '[1] "Product Information. Seysara (sarecycline)." Allergan Inc, Irvine, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[4] "Product Information. Fosrenol (lanthanum carbonate)." Shire US Inc, Florence, KY.[5] "Product Information. Nuzyra (omadacycline)." Paratek Pharmaceuticals, Inc., Boston, MA.[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59716, 'Carvedilol', 'Larotrectinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4, P-glycoprotein (P-gp), and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of larotrectinib.', NULL, 'Caution is advised when larotrectinib is used with CYP450 3A4, P-gp, and/or BCRP inhibitors.', NULL, 'Caution is advised when larotrectinib is used with CYP450 3A4, P-gp, and/or BCRP inhibitors. Patients should be monitored for adverse effects such as neurotoxicity (delirium, dysarthria, dizziness, gait disturbance, paraesthesia, encephalopathy, memory impairment, tremor) and hepatotoxicity (elevations in liver transaminases), and the larotrectinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59798/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Vitrakvi (larotrectinib)." Bayer Pharmaceutical Inc, West Haven, CT.[4] "Product Information. Vitrakvi (larotrectinib)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59717, 'Carvedilol', 'Lasmiditan', 'Moderate', 'Coadministration of lasmiditan and agents that lower heart rate may increase the risk of bradycardia.', NULL, 'Caution is advised during coadministration of lasmiditan and agents that lower heart rate if the estimated decrease in heart rate may present a clinical risk.', NULL, 'Caution is advised during coadministration of lasmiditan and agents that lower heart rate if the estimated decrease in heart rate may present a clinical risk. Consider monitoring heart rate in patients for whom the estimated decrease in heart rate may not be tolerated.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59799/', '[1] "Product Information. Reyvow (lasmiditan)." Lilly, Eli and Company, Indianapolis, IN.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59718, 'Carvedilol', 'Lefamulin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-gp may increase the plasma concentrations of lefamulin, which is a substrate of both the isoenzyme and efflux transporter.', NULL, 'Caution is advised when lefamulin is used with CYP450 3A4 and/or P-gp inhibitors.', NULL, 'Caution is advised when lefamulin is used with CYP450 3A4 and/or P-gp inhibitors. Patients should be monitored for increased adverse effects and seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59800/', '[1] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc., King of Prussia, PA.[2] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc., King of Prussia, PA.[3] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.[4] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc., King of Prussia, PA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59719, 'Carvedilol', 'Levosalbutamol', 'Major', 'Beta-blockers may antagonize the effects of beta-2 adrenergic bronchodilators and precipitate acute, life-threatening bronchospasm in patients with asthma or other obstructive airway diseases. The mechanism involves increased airway resistance and reduced bronchodilation due to blockade of beta-2 adrenergic receptors. The interaction may also occur with ophthalmically applied beta-blockers, which are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels. Due to opposing effects on beta-2 adrenergic receptors, propranolol has been used in the treatment of albuterol overdose. Likewise, beta-2 adrenergic agonists may interfere with the pharmacologic effects of beta-blockers.', NULL, 'Concomitant use of beta-2 adrenergic bronchodilators with beta-blockers, including ophthalmic formulations, should generally be avoided.', NULL, 'Concomitant use of beta-2 adrenergic bronchodilators with beta-blockers, including ophthalmic formulations, should generally be avoided. If coadministration is required, a cardioselective beta-blocker (e.g., acebutolol, atenolol, betaxolol, bisoprolol, metoprolol, nebivolol) is usually preferred. Nevertheless, caution is advised and respiratory status should be closely monitored, as cardioselectivity is not absolute and larger doses of beta-1 selective agents may pose some of the same risks as nonselective agents. In general, nonselective beta-blockers are considered contraindicated in patients with obstructive airways disease.', 'Antagonism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59801/', '[1] Craig TJ "Drugs to be used with caution in patients with asthma." Am Fam Physician 54 (1996): 947-53[2] Chafin CC, Soberman JE, Demirkan K, Self T "Beta-blockers after myocardial infarction: Do benefits ever outweigh risks in asthma?." Cardiology 92 (1999): 99-105[3] Chodosh S, Tuck J, Blasucci DJ "The effects of dilevalol, metoprolol, and placebo on ventilatory function in asthmatics." J Cardiovasc Pharmacol 11 (1988): s18-24[4] Gold MR, Dec GW, Cocca-Spofford D, Thompson BT "Esmolol and ventilatory function in cardiac patients with COPD." Chest 100 (1991): 1215-8[5] Sheppard D, DiStefano S, Byrd RC, Eschenbacher WL, Bell V, Steck J, Laddu A "Effects of esmolol on airway function in patients with asthma." J Clin Pharmacol 26 (1986): 169-74[6] "Product Information. Corgard (nadolol)." Bristol-Myers Squibb, Princeton, NJ.[7] "Product Information. Betagan Liquifilm (levobunolol)." Allergan Inc, Irvine, CA.[8] "Product Information. Blocadren (timolol)." Merck & Co, Inc, West Point, PA.[9] Ruffin RE, Frith PA, Anderton RC, Kumana CR, Newhouse MT, Hargreave FE "Selectivity of beta adrenoreceptor antagonist drugs assessed by histamine bronchial provocation." Clin Pharmacol Ther 25 (1979): 536-40[10] Dunn TL, Gerber MJ, Shen AS, Fernandez E, Iseman MD, Cherniak RM "The effect of topical ophthalmic instillation of timolol and betaxolol on lung function in asthmatic subjects." Am Rev Respir Dis 133 (1986): 264-8[11] Brooks AM, Burden JG, Gillies WE "The significance of reactions to betaxolol reported by patients." Aust N Z J Ophthalmol 17 (1989): 353-5[12] Salpeter SS, Ormiston T, Salpeter E, Poole P, Cates D "Cardioselective beta-blockers for chronic obstructive pulmonary disease." Cochrane Database Syst Rev 2 (2002): CD0003566[13] Brooks AM, Gillies WE "Ocular beta-blockers in glaucoma management. Clinical pharmacological aspects." Drugs Aging 2 (1992): 208-21[14] "Product Information. Inderal (propranolol)." Wyeth-Ayerst Laboratories, Ph