INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59746, 'Carvedilol', 'Mepenzolate', 'Moderate', 'Anticholinergic agents frequently cause drowsiness and other central nervous system-depressant effects that may be additive with those induced by beta blockers. In addition, these agents may increase heart rate and theoretically may counteract the bradycardic effects of beta blockers. Pharmacokinetically, anticholinergic agents may delay the gastrointestinal absorption of beta blockers and other drugs that are administered orally. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents.', NULL, 'Patients should be monitored for potentially excessive CNS adverse effects (e.', NULL, 'Patients should be monitored for potentially excessive CNS adverse effects (e.g., drowsiness, dizziness, lightheadedness, confusion, blurred vision) if anticholinergic agents are used in combination with beta blockers. Patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism, Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59828/', '[1] Regardh CG, Lundborg P, Persson BA "The effect of antacid, metoclopramide, and propantheline on the bioavailability of metoprolol and atenolol." Biopharm Drug Dispos 2 (1981): 79-87[2] Briant RH, Dorrington RE, Ferry DG, Paxton JW "Bioavailability of metoprolol in young adults and the elderly, with additional studies on the effects of metoclopramide and probanthine." Eur J Clin Pharmacol 25 (1983): 353-6[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[4] Clark JM, Seager SJ "Gastric emptying following premedication with glycopyrrolate or atropine." Br J Anaesth 55 (1983): 1195-9', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59747, 'Carvedilol', 'Meperidine', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59829/', '[1] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991): 139[2] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990): 385-6[3] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991): 390-1[4] Pacher P, Kecskemeti V "Cardiovascular side effects of new antidepressants and antipsychotics: new drugs, old concerns?" Curr Pharm Des 10 (2004): 2463-75[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Andrews C, Pinner G "Postural hypotension induced by paroxetine." BMJ 316 (1998): 595[7] Rodriguez de la Torre B, Dreher J, Malevany I, et al. "Serum levels and cardiovascular effects of tricyclic antidepressants and selective serotonin reuptake inhibitors in depressed patients." Ther Drug Monit 23 (2001): 435-40[8] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[9] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[10] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[11] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59748, 'Carvedilol', 'Mephentermine', 'Moderate', 'Beta-blockers may antagonize the cardiostimulatory effects of pressor agents by blocking beta-1 adrenergic receptors in the heart. Vasopressors may be less effective if the patient is receiving, or has recently received, a beta-blocking drug. In addition, peripheral vascular resistance may increase due to unopposed alpha-adrenergic effects of pressor agents in the presence of beta-blockade. Theoretically, the interaction may also occur with beta-blocker ophthalmic preparations, since they may be systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', NULL, 'No specific intervention is necessary, but clinicians should be alert to the potential for diminished cardiac response when pressor agents are used in patients treated with beta-blockers, including ophthalmic formulations.', NULL, 'No specific intervention is necessary, but clinicians should be alert to the potential for diminished cardiac response when pressor agents are used in patients treated with beta-blockers, including ophthalmic formulations. A beta-blocker such as propranolol may be used to treat cardiac arrhythmias that occur during administration of pressor agents like mephentermine, metaraminol, norepinephrine, or phenylephrine.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59830/', '[1] "Product Information. Dobutrex (dobutamine)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Intropin (dopamine)." DuPont Pharmaceuticals, Wilmington, DE.[3] Richards DA, Prichard BN, Hernandez R "Circulatory effects of noradrenaline and adrenaline before and after labetalol." Br J Clin Pharmacol 7 (1979): 371-8', 1767365204);
