INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59849, 'Carvedilol', 'Ranolazine', 'Moderate', 'Coadministration with ranolazine may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. Ranolazine has been shown in vitro to be an inhibitor of CYP450 2D6. However, concomitant use of ranolazine with other drugs that are metabolized by CYP450 2D6 such as tricyclic antidepressants and antipsychotics has not been studied.', NULL, 'Caution is advised if ranolazine must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if ranolazine must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ranolazine is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59931/', '[1] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.[2] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59850, 'Carvedilol', 'Rapacuronium', 'Moderate', 'Neuromuscular blocking agents and beta-blockers may have additive cardiovascular depressant effects. Limited data suggest that beta-blockers may modestly potentiate the pharmacologic effects of neuromuscular blocking agents. The exact mechanism of interaction is unknown but may involve effects in the postsynaptic membrane, as beta-blockers alone have been reported to exacerbate or unmask myasthenia gravis.', NULL, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers.', NULL, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers. Respiratory and cardiovascular status should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59932/', '[1] Loan PB, Connolly FM, Mirakhur RK, Kumar N, Farling P "Neuromuscular effects of rocuronium in patients receiving beta-adrenoreceptor blocking, calcium entry blocking and anticonvulsant drugs." Br J Anaesth 78 (1997): 90-1[2] Harrah MD, Way WL, Katzung BG "The interaction of d-tubocurarine with antiarrhythmic drugs." Anesthesiology 33 (1970): 406-10[3] Rozen MS, Whan FM "Prolonged curarization associated with propranolol." Med J Aust 1 (1972): 467-8[4] Murthy VS, Patel KD, Elangovan RG, Hwang TF, Solochek SM, Steck JD, Laddu AR "Cardiovascular and neuromuscular effects of esmolol during induction of anesthesia." J Clin Pharmacol 26 (1986): 351-7[5] Glynne GL "Drug interaction?" Anaesthesia 39 (1984): 293[6] Herishanu Y, Rosenberg P "Beta-blockers and myasthenia gravis." Ann Intern Med 83 (1975): 834-5[7] Yate B, Mostafa SM "Drug interaction?" Anaesthesia 39 (1984): 728-9[8] Chapple DJ, Clark JS, Hughes R "Interaction between atracurium and drugs used in anaesthesia." Br J Anaesth 55 Suppl 1 (1983): s17-22', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59851, 'Carvedilol', 'Rasagiline', 'Moderate', 'Sympathetic ganglion-blocking or catecholamine-depleting agents such as guanethidine, reserpine, and monoamine oxidase (MAO) inhibitors may potentiate the pharmacologic effects of beta-blockers, which are thought to competitively antagonize catecholamines at cardiac and other peripheral adrenergic neurons. Combining these medications may increase the risk of hypotension, orthostasis, bradycardia, and heart failure due to excessive reduction of sympathetic activity.', NULL, 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents.', NULL, 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents. Patients should contact their doctor if they experience dizziness, lightheadedness, syncope, bradycardia, shortness of breath, difficulty breathing, edema, and/or chest pain.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59933/', '[1] "Product Information. Marplan (isocarboxazid)" Roche Laboratories, Nutley, NJ.[2] "Product Information. Matulane (procarbazine)." Roche Laboratories, Nutley, NJ.[3] Kronig MH, Roose SP, Walsh BT, Woodring S, Glassman AH "Blood pressure effects of phenelzine." J Clin Psychopharmacol 3 (1983): 307-10[4] "Product Information. Nardil (phenelzine)." Parke-Dvis, Morris Plains, NJ.[5] Golwyn DH, Sevlie CP "Monoamine oxidase inhibitor hypertensive crisis headache and orthostatic hypotension." J Clin Psychopharmacol 13 (1993): 77-8[6] "Product Information. Parnate (tranylcypromine)." SmithKline Beecham, Philadelphia, PA.[7] De Vita VT, Hahn MA, Oliverio VT "Monoamine oxidase inhibition by a new carcinostatic agent, n-isopropyl-a-(2-methylhydrazino)-p-toluamide (MIH). (30590)." Proc Soc Exp Biol Med 120 (1965): 561-5[8] Pettinger WA, Soyangco FG, Oates JA "Inhibition of monoamine oxidase in man by furazolidone." Clin Pharmacol Ther 9 (1968): 442-7[9] "Product Information. Hylorel (guanadrel)." Rhone-Poulenc Rorer, Collegeville, PA.[10] Reggev A, Vollhardt BR "Bradycardia induced by an interaction between phenelzine and beta blockers." Psychosomatics 30 (1989): 106-8[11] Goldberg LI "Monoamine oxidase inhibitors: adverse reactions and possible mechanisms." JAMA 190 (1964): 456-62[12] "Product Information. Toprol-XL (metoprolol)" Astra-Zeneca Pharmaceuticals, Wilmington, DE.[13] Kraus RP "Pindolol augmentation of tranylcypromine in psychotic depression." J Clin Psychopharmacol 17 (1997): 225-6', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59852, 'Carvedilol', 'Relugolix', 'Major', 'Coadministration with inhibitors of the P-glycoprotein (P-gp) efflux transporter may increase the plasma concentrations of relugolix, particularly when the inhibitors are given orally. Relugolix is a substrate for intestinal P-gp.', NULL, 'Concomitant use of relugolix with orally administered P-gp inhibitors should generally be avoided.', NULL, 'Concomitant use of relugolix with orally administered P-gp inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends taking relugolix first and separating the dosing by at least 6 hours. Patients should be monitored more frequently for adverse effects. Alternatively, treatment with relugolix may be interrupted for up to two weeks if a short course of treatment with a P-gp inhibitor is necessary.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59934/', '[1] "Product Information. Orgovyx (relugolix)." Myovant Sciences, Inc., Brisbane, CA.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59853, 'Carvedilol', 'Remimazolam', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59935/', '[1] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991): 139[2] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990): 385-6[3] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991): 390-1[4] Pacher P, Kecskemeti V "Cardiovascular side effects of new antidepressants and antipsychotics: new drugs, old concerns?" Curr Pharm Des 10 (2004): 2463-75[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Andrews C, Pinner G "Postural hypotension induced by paroxetine." BMJ 316 (1998): 595[7] Rodriguez de la Torre B, Dreher J, Malevany I, et al. "Serum levels and cardiovascular effects of tricyclic antidepressants and selective serotonin reuptake inhibitors in depressed patients." Ther Drug Monit 23 (2001): 435-40[8] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[9] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[10] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[11] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59854, 'Carvedilol', 'Repaglinide', 'Moderate', 'Beta-blockers may inhibit some of the normal physiologic response to hypoglycemia. Symptoms of hypoglycemia such as tremor and tachycardia may be absent, making it more difficult for patients to recognize an oncoming episode. Specifically, inhibition of catecholamine-mediated glycogenolysis and glucose mobilization in association with beta-blockade can potentiate insulin-induced hypoglycemia in diabetics and delay the recovery of normal blood glucose levels. Prolonged and severe hypoglycemia may occur, although these events have rarely been reported. Significant increases in blood pressure and bradycardia can also occur during hypoglycemia in diabetics treated with insulin and beta-blockers due to antagonism of epinephrine''s effect on beta-2 adrenergic receptors, which leads to unopposed alpha-adrenergic effects including vasoconstriction. Other effects reported with various beta-blockers include decreased glucose tolerance and decreased glucose-induced insulin secretion.', NULL, 'In general, cardioselective beta-blockers are considered safer than noncardioselective agents in the treatment of diabetic patients.', NULL, 'In general, cardioselective beta-blockers are considered safer than noncardioselective agents in the treatment of diabetic patients. Patients should be advised of the need for regular blood glucose monitoring and be aware that certain symptoms of hypoglycemia such as tremor and tachycardia may be masked. However, other symptoms such as headache, dizziness, drowsiness, confusion, nausea, hunger, weakness, and perspiration may be unaffected.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pha