INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59874, 'Carvedilol', 'Sildenafil', 'Moderate', 'Phosphodiesterase-5 (PDE5) inhibitors may potentiate the blood pressure-lowering effect of antihypertensive medications. These agents inhibit PDE5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can lead to peripheral vasodilation.', NULL, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents.', NULL, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents. Patients receiving the combination should be advised to avoid rising abruptly from a sitting or recumbent position, especially following treatment initiation or a dosage increase, and to contact their doctor if they experience symptoms of hypotension such as dizziness, lightheadedness, fainting, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59956/', '[1] "Product Information. Stendra (avanafil)." Vivus Inc, Mountain View, CA.[2] "Product Information. Viagra (sildenafil)." Pfizer US Pharmaceuticals, New York, NY.[3] "Product Information. Levitra (vardenafil)." Bayer, West Haven, CT.[4] Jetter A, Kinzig-Schippers M, Walchner-Bonjean M, et al. "Effects of grapefruit juice on the pharmacokinetics of sildenafil." Clin Pharmacol Ther 71 (2002): 21-29', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59875, 'Carvedilol', 'Silodosin', 'Moderate', 'Additive hypotensive effects may occur when beta-blockers are used in combination with alpha-blockers. In the presence of beta-blockade, the risk and/or severity of first-dose effects associated with alpha-blockers such as postural hypotension and syncope may be increased. Beta-blockers may also blunt the reflex tachycardia that occurs in response to postural hypotension. Theoretically, the interaction may also occur with beta-blocker ophthalmic preparations, since they may be systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', NULL, 'Caution is advised during coadministration of these agents, particularly when initiating an alpha-blocker in the presence of a beta-blocker, including ophthalmic formulations.', NULL, 'Caution is advised during coadministration of these agents, particularly when initiating an alpha-blocker in the presence of a beta-blocker, including ophthalmic formulations. Small initial dosages of the alpha-blocker should be considered and gradually titrated to desired effect, while the systemic beta-blocker dosage may also need to be reduced. Hemodynamic responses should be monitored, especially during the first few weeks of therapy. Taking the alpha-blocker at bedtime may minimize the occurrence of orthostatic effects. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59957/', '[1] Holtzman JL, Kaihlanen PM, Rider JA, Lewin AJ, Spindler JS, Oberlin JA "Concomitant administration of terazosin and atenolol for the treatment of essential hypertension." Arch Intern Med 148 (1988): 539-43[2] Chrysant SG "Experience with terazosin administered in combination with other antihypertensive agents." Am J Med 80 (1986): 55-61[3] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[4] Pool JL "Combination antihypertensive therapy with terazosin and other antihypertensive agents: results of clinical trials." Am Heart J 122 (1991): 926-31[5] Rubin P, Jackson G, Blaschke T "Studies on the clinical pharmacology of prazosin II: the influence of indomethacin and of propranolol on the action and disposition of prazosin." Br J Clin Pharmacol 10 (1980): 33-9[6] Elliott HL, McLean K, Sumner DJ, Meredith PA, Reid JL "Immediate cardiovascular responses to oral prazosin: effects of concurrent beta-blockers." Clin Pharmacol Ther 29 (1981): 303-9[7] Seideman P, Grahnen A, Haglund K, Lindstrom B, Von Bahr C "Prazosin first dose phenomenon during combined treatment with a B-adrenoceptor blocker in hypertensive patients." Br J Clin Pharmacol 13 (1982): 865-70[8] Stokes GS, Mennie BA, Gellatly R, Hill A "On the combination of alpha- and beta-adrenoceptor blockade in hypertension." Clin Pharmacol Ther 34 (1983): 576-82', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59876, 'Carvedilol', 'Siponimod', 'Major', 'The risk of severe bradycardia and atrioventricular (AV) block may be increased during initiation of siponimod treatment in patients receiving other drugs that slow heart rate or AV conduction such as beta-blockers, certain calcium channel blockers (e.g., diltiazem, verapamil), and digitalis.', NULL, 'Siponimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate.', NULL, 'Siponimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate. Treatment with siponimod should generally not be initiated in patients who are concurrently treated with heart rate-lowering drugs. Advice from a cardiologist should be sought if coadministration of siponimod and drugs that slow heart rate or AV conduction is considered.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59958/', '[1] "Product Information. Mayzent (siponimod)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59877, 'Carvedilol', 'Nitrous acid', 'Moderate', 'Coadministration with antihypertensive agents, diuretics, vasodilators, or phosphodiesterase-5 (PDE5) inhibitors may potentiate the hypotensive effect of sodium nitrite. Since sodium nitrite can cause serious hypotension and methemoglobin formation, patients may be at increased risk for complications related to inadequate perfusion and oxygenation.', NULL, 'Sodium nitrite should be used with caution in the presence of concomitant antihypertensive agents, diuretics, vasodilators, or PDE5 inhibitors.', NULL, 'Sodium nitrite should be used with caution in the presence of concomitant antihypertensive agents, diuretics, vasodilators, or PDE5 inhibitors. Hemodynamics should be monitored during and after administration of sodium nitrite, and the infusion rate decreased if significant hypotension occurs. In addition, methemoglobin levels should be monitored and oxygen administered during treatment whenever possible.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59959/', '[1] "Product Information. Sodium Nitrite (sodium nitrite)." Hope Pharmaceuticals, Scottsdale, AZ.[2] Gunston GD, Mehta U "Potentially serious drug interactions with grapefruit juice." S Afr Med J 90 (2000): 41[3] Fuhr U, Maier-Bruggemann A, Blume H, et al. "Grapefruit juice increases oral nimodipine bioavailability." Int J Clin Pharmacol Ther 36 (1998): 126-32[4] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[5] "Grapefruit ju