INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (59901, 'Carvedilol', 'Thiothixene', 'Moderate', 'Some antipsychotic agents and some beta-blockers may mutually inhibit each other''s metabolism resulting in increased or additive pharmacologic effects such as hypotension, bradycardia, delirium or seizures. The mechanism may be related to competitive inhibition of CYP450 2D6-mediated first-pass metabolism.', NULL, 'Atenolol, carteolol, or nadolol may be considered as alternatives because they undergo renal elimination and are not expected to interact.', NULL, 'Atenolol, carteolol, or nadolol may be considered as alternatives because they undergo renal elimination and are not expected to interact. If this combination cannot be avoided, decreased dosage of either or both drugs may be necessary and the patient should be closely monitored for cardiovascular or nervous system adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59983/', '[1] Silver JM, Yudofsky SC, Kogan M, Katz BL "Elevation of thioridazine plasma levels by propranolol." Am J Psychiatry 143 (1986): 1290-2[2] Peet M, Middlemiss D, Yates R "Pharmacokinetic interaction between propranolol and chlorpromazine in schizophrenic patients." Lancet 2 (1980): 978[3] Markowitz JS, Wells BG, Carson WH "Interactions between antipsychotic and antihypertensive drugs." Ann Pharmacother 29 (1995): 603-9[4] Miller F, Rampling D "Adverse effects of combined propranolol and chlorpromazine therapy." Am J Psychiatry 139 (1982): 1198-9[5] Vestal RE, Kornhauser DM, Hollifield JW, Shand DG "Inhibition of propranolol metabolism by chlorpromazine." Clin Pharmacol Ther 25 (1979): 19-24', 1