INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119088, 'Irinotecan', 'Mitotane', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of irinotecan and its pharmacologically active metabolite, SN-38. Irinotecan is partially metabolized by CYP450 3A4 to inactive substances, and induction of this process results in less of the drug available for conversion to SN-38 via hepatic carboxylesterases. Available data also suggest induction of other enzymatic pathways (e.g., UGT1A1; carboxylesterases) and drug transporters (e.g., multispecific organic anion transporter, or MRP2; mitoxantrone-resistance half transporter protein, or MXR) that may be involved in the clearance of irinotecan and/or SN-38, although the extent to which they contribute to the interaction is unknown.', NULL, 'Concomitant use of irinotecan with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of irinotecan with potent CYP450 3A4 inducers should generally be avoided. Consideration should be given to substituting nonenzyme-inducing agents at least one to two weeks prior to initiation of irinotecan therapy whenever possible.', 'Metabolism', 'Pertuzumab, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Porfimer sodium, Selinexor, Aflibercept, More', 'Vincristine, Vinblastine, Topotecan, Teniposide, Vinorelbine', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119226/', '[1] Kuhn JG "Influence of anticonvulsants on the metabolism and elimination of irinotecan. A North American Brain Tumor Consortium preliminary report." Oncology (Williston Park 16(8 Suppl 7)