INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119166, 'Irinotecan', 'Tafamidis', 'Moderate', 'Based on in vitro data, coadministration with tafamidis may increase the plasma concentrations and the risk of toxicity of drugs that are substrates of the transport protein breast cancer resistance protein (BCRP) (e.g., methotrexate, rosuvastatin, imatinib), uridine diphosphate glucuronosyltransferase (UGT) 1A1 (e.g., bictegravir, irinotecan, raltegravir), and/or uptake transporters OAT1 and OAT3 (organic anion transporters) (e.g., bumetanide, furosemide, lamivudine, methotrexate, oseltamivir, tenofovir, ganciclovir, adefovir, cidofovir, zidovudine, zalcitabine) at clinically relevant concentrations. The proposed mechanism is decreased clearance due to tafamidis-mediated inhibition of the corresponding transport protein.', NULL, 'Caution is advised if tafamidis is used concomitantly with drugs that are substrates of these transport proteins, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if tafamidis is used concomitantly with drugs that are substrates of these transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever tafamidis is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Excretion', 'Trabectedin, Cabazitaxel, Vinblastine, Paclitaxel, Vincristine, Topotecan, Teniposide, Vinorelbine, Etoposide, Docetaxel', 'Dextromethorphan, Valbenazine, Dalfampridine, Sodium oxybate, Riluzole, Tetrabenazine, Inotersen, Amifampridine, Deutetrabenazine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'h