INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119368, 'Isavuconazonium', 'Fostamatinib', 'Moderate', 'Coadministration of fostamatinib with inhibitors of CYP450 3A4 may increase exposure to the active metabolite known as R406, the predominant moiety in the systemic circulation following fostamatinib administration. Fostamatinib is metabolized in the gut by alkaline phosphatase to R406, which then undergoes oxidation via CYP450 3A4 and glucuronidation via UGT1A9. In vitro, R406 is also a substrate of the P-glycoprotein (P-gp) efflux transporter.', NULL, 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent or moderate CYP450 3A4 inhibitors or dual CYP450 3A4/P-gp inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', NULL, 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent or moderate CYP450 3A4 inhibitors or dual CYP450 3A4/P-gp inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Eltrombopag, Romiplostim, Avatrombopag, Emicizumab, Coagulation factor VIIa Recombinant Human, Coagulation factor X human, Phylloquinone, Anti-inhibitor coagulant complex, Antihemophilic factor, human recombinant, Fibrinogen human, Epinephrine, Coagulation Factor IX Human', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119506/', '[1] "Product Information. Tavalisse (fostamatinib)." Rigel Pharmaceuticals, South San Francisco, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119369, 'Isavuconazonium', 'Galantamine', 'Moderate', 'Coadministration with isavuconazonium sulfate (prodrug of isavuconazole) may increase the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or P-glycoprotein (P-gp). The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity and/or P-gp-mediated efflux of these drugs by isavuconazole.', NULL, 'Caution is advised when isavuconazonium sulfate is used concomitantly with drugs that are substrates of the CYP450 3A4 isoenzyme or the P-gp transport protein, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when isavuconazonium sulfate is used concomitantly with drugs that are substrates of the CYP450 3A4 isoenzyme or the P-gp transport protein, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever isavuconazonium sulfate is added to or withdrawn from therapy.', 'Metabolism', 'Rivastigmine, Aducanumab, Memantine, Ginkgo biloba, Tacrine', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119507/', '[1] "Product Information. Cresemba (isavuconazonium)." Astellas Pharma US, Inc, Deerfield, IL.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Ambien (zolpidem)." sanofi-aventis, Bridgewater, NJ.[4] Yamreudeewong W, Henann NE, Fazio A, Lower DL, Cassidy TG "Drug-food interactions in clinical practice." J Fam Pract 40 (1995): 376-84', 1767369485);
