INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119387, 'Isavuconazonium', 'Indinavir', 'Major', 'Coadministration of isavuconazonium sulfate (prodrug of isavuconazole) and indinavir may increase the plasma concentrations of isavuconazole and decrease the plasma concentrations of indinavir. Both drugs are substrates as well as inhibitors of CYP450 3A4/5. The proposed mechanism for isavuconazole involves indinavir inhibition of CYP450 3A4/5-mediated metabolism of isavuconazole. The reduction in indinavir plasma concentrations occurs by an undetermined mechanism.', NULL, 'Concomitant use of isavuconazonium sulfate with potent CYP450 3A4 inhibitors, such as indinavir, is considered contraindicated.', NULL, 'Concomitant use of isavuconazonium sulfate with potent CYP450 3A4 inhibitors, such as indinavir, is considered contraindicated.', 'Metabolism', 'Lamivudine, Glecaprevir, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119525/', '[1] "Product Information. Cresemba (isavuconazonium)." Astellas Pharma US, Inc, Deerfield, IL.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Yeh KC, Deutsch PJ, Haddix H, Hesney M, Hoagland V, Ju WD, Justice SJ, Osborne B, Sterrett AT, Stone JA, Woolf E, Waldman S "Single-dose pharmacokinetics of indinavir and the effect of food." Antimicrob Agents Chemother 42 (1998): 332-8[4] Shelton MJ, Wynn HE, Newitt RG, DiFrancesco R "Effects of grapefruit juice on pharmacokinetic exposure to indinavir in HIV-positive subjects." J Clin Pharmacol 41 (2001): 435-42[5] "Product Information. Crixiva