INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119453, 'Isavuconazonium', 'Ondansetron', 'Moderate', 'Coadministration with isavuconazonium sulfate (prodrug of isavuconazole) may increase the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or P-glycoprotein (P-gp). The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity and/or P-gp-mediated efflux of these drugs by isavuconazole.', NULL, 'Caution is advised when isavuconazonium sulfate is used concomitantly with drugs that are substrates of the CYP450 3A4 isoenzyme or the P-gp transport protein, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when isavuconazonium sulfate is used concomitantly with drugs that are substrates of the CYP450 3A4 isoenzyme or the P-gp transport protein, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever isavuconazonium sulfate is added to or withdrawn from therapy.', 'Metabolism', 'Nabilone, Scopolamine, Dronabinol, Rolapitant, Dolasetron, Palonosetron', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119591/', '[1] "Product Information. Cresemba (isavuconazonium)." Astellas Pharma US, Inc, Deerfield, IL.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Ambien (zolpidem)." sanofi-aventis, Bridgewater, NJ.[4] Yamreudeewong W, Henann NE, Fazio A, Lower DL, Cassidy TG "Drug-food interactions in clinical practice." J Fam Pract 40 (1995): 376-84', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119454, 'Isavuconazonium', 'Osilodrostat', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of osilodrostat, which is partially metabolized by the isoenzyme.', NULL, 'Caution is advised when osilodrostat is coadministered with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when osilodrostat is coadministered with CYP450 3A4 inhibitors. Dosage adjustments may be required based on clinical response and tolerance. Patients should have regular monitoring of 24-hour urine free cortisol and serum or plasma cortisol during treatment, as well as regular evaluations for signs and symptoms of hypocortisolism such as nausea, vomiting, abdominal pain, loss of appetite, fatigue, dizziness, hypotension, abnormal electrolyte levels, and hypoglycemia. Decrease dosing or temporarily discontinue osilodrostat if patients experience symptoms of hypocortisolism or if urine free cortisol levels fall below the target range or there is a rapid decrease in cortisol levels. Stop osilodrostat and administer exogenous glucocorticoid replacement therapy if patients have symptoms of adrenal insufficiency and serum or plasma cortisol levels are below target range. Osilodrostat therapy may be restarted at a lower dosage when symptoms have resolved and cortisol values are within target range.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119592/', '[1] "Product Information. Isturisa (osilodrostat)." Recordati Rare Diseases Inc, Lebanon, NJ.[2] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
