INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119839, 'Isocarboxazid', 'Thalidomide', 'Moderate', 'Thalidomide can commonly cause drowsiness/sedation and may have additive effects with other drugs that cause central nervous system (CNS) depression.', NULL, 'Caution is advised when thalidomide is used with other CNS depressants such as anticonvulsants, antihistamines, antidepressants, antipsychotics, anxiolytics, opioids, and sedatives/hypnotics.', NULL, 'Caution is advised when thalidomide is used with other CNS depressants such as anticonvulsants, antihistamines, antidepressants, antipsychotics, anxiolytics, opioids, and sedatives/hypnotics. Patients should be advised to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities. Dosage reductions may be necessary. Thalidomide should preferably be administered at bedtime to reduce the impact of somnolence. Alcohol use is not recommended.', 'Synergism', 'Golimumab, Cyclosporine, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, More', 'Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, Vilazodone', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119977/', '[1] "Product Information. Thalomid (thalidomide)." Celgene Corporation, Warren, NJ.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (19