INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1359394, 'Daptomycin', 'Atorvastatin', 'Moderate', 'There is the potential for additive musculoskeletal toxicity during coadministration of daptomycin and HMG-CoA reductase inhibitors. This may be due to additive musculoskeletal toxicity as both HMG-CoA reductase inhibitors and daptomycin have caused myopathy and rhabdomyolysis when given alone.', NULL, 'Use of HMG-CoA reductase inhibitors should be temporarily suspended in patients receiving daptomycin.', NULL, 'Use of HMG-CoA reductase inhibitors should be temporarily suspended in patients receiving daptomycin. All patients receiving daptomycin should be monitored for the development of muscle pain or weakness, particularly of the distal extremities. CPK levels should be monitored weekly, and patients who develop unexplained elevations in CPK should be monitored more frequently. Daptomycin should be discontinued in patients with unexplained signs and symptoms of myopathy in conjunction with CPK levels exceeding 1000 U/L (approximately 5 times the upper limit of normal), or in patients without reported symptoms who have marked elevations in CPK (10 times the upper limit of normal, or more).', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
