        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1359395, 'Darolutamide', 'Atorvastatin', 'Moderate', 'Coadministration with darolutamide may increase the plasma concentrations of drugs that are substrates of the breast cancer resistance protein (BCRP) transporter. The proposed mechanism is decreased clearance due to inhibition of BCRP-mediated intestinal and hepatobiliary efflux by darolutamide.', NULL, 'Concomitant use of darolutamide with drugs that are BCRP substrates should be avoided when possible.', NULL, 'Concomitant use of darolutamide with drugs that are BCRP substrates should be avoided when possible. If coadministration is required, patients should be monitored carefully for increased adverse reactions of these drugs and dosing adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
