        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1359588, 'Didanosine', 'Atovaquone', 'Minor', 'Coadministration with atovaquone has been shown to decrease the systemic exposure (AUC) of didanosine by 24% in a multi-dose drug interaction study. The mechanism is unknown, although the interaction is unlikely to be of clinical significance. Didanosine had no effect on the pharmacokinetics of atovaquone.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
