        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1359589, 'Efavirenz', 'Atovaquone', 'Major', 'Coadministration with efavirenz may significantly decrease the plasma concentrations of atovaquone and proguanil. The mechanism of interaction has not been established, but may involve induction of the glucuronidation of atovaquone and the CYP450 2C19-mediated metabolism of proguanil.', NULL, 'Given the potential for treatment failure when atovaquone or atovaquone/proguanil is administered with efavirenz, concomitant use is not recommended.', NULL, 'Given the potential for treatment failure when atovaquone or atovaquone/proguanil is administered with efavirenz, concomitant use is not recommended.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
