        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1359591, 'Etoposide', 'Atovaquone', 'Moderate', 'The coadministration with atovaquone may modestly increase the plasma concentrations of etoposide and its catechol metabolite. The mechanism of interaction is unknown.', NULL, 'Given the risk of etoposide-related secondary acute myeloid leukemia associated with minor changes in schedule and concurrent therapy, caution is advised during the simultaneous administration of atovaquone and etoposide.', NULL, 'Given the risk of etoposide-related secondary acute myeloid leukemia associated with minor changes in schedule and concurrent therapy, caution is advised during the simultaneous administration of atovaquone and etoposide. Patients should be monitored for etoposide toxicity such as myelosuppression.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
