        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1359593, 'Indinavir', 'Atovaquone', 'Moderate', 'Coadministration of atovaquone (750 mg twice a day with food) and indinavir (800 mg three times a day without food) for 14 days has been shown to decrease the trough plasma concentration (Cmin) of indinavir by 23%. The mechanism and clinical significance of this interaction are unknown. Steady-state indinavir peak plasma concentration (Cmax) and systemic exposure (AUC) were not altered by atovaquone.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is recommended in patients receiving indinavir in combination with atovaquone.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is recommended in patients receiving indinavir in combination with atovaquone. Antiretroviral response should be closely monitored.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
