        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1359600, 'Atovaquone', 'Metoclopramide', 'Moderate', 'Concomitant treatment with metoclopramide has been shown to reduce the bioavailability of atovaquone. The mechanism of interaction is unknown.', NULL, 'Patients on atovaquone requiring an antiemetic should only be prescribed metoclopramide if other antiemetics are unavailable.', NULL, 'Patients on atovaquone requiring an antiemetic should only be prescribed metoclopramide if other antiemetics are unavailable. Clinical response to atovaquone and parasitemia should be closely monitored if metoclopramide is used.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
