    ) VALUES (1359608, 'Rifabutin', 'Atovaquone', 'Moderate', 'Coadministration with a rifamycin may decrease the plasma concentrations of atovaquone. The exact mechanism is unknown but may be related to the enzyme-inducing effects of rifamycins.', NULL, 'The use of atovaquone in combination with rifampin or rifabutin is not recommended.', NULL, 'The use of atovaquone in combination with rifampin or rifabutin is not recommended. The same precaution may be applicable to rifapentine, although clinical data are lacking.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
