    ) VALUES (1359614, 'Sulfamethoxazole', 'Atovaquone', 'Minor', 'Coadministration with atovaquone has been shown to slightly decrease the plasma concentrations of trimethoprim (TMP) and sulfamethoxazole (SMX). The mechanism of interaction is unknown. TMP-SMX had no effect on the pharmacokinetics of atovaquone.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
