    ) VALUES (1359648, 'Atropine', 'Arbutamine', 'Moderate', 'Atropine and other agents with significant anticholinergic activity (e.g., clozapine) may enhance the chronotropic response to arbutamine due to their blockade of normal vagal inhibition of the sinoatrial node, resulting in increased sinoatrial node automaticity.', NULL, 'As the dosing of arbutamine is based on the heart rate response of the patient, the concomitant use of atropine or other anticholinergic agents should be avoided.', NULL, 'As the dosing of arbutamine is based on the heart rate response of the patient, the concomitant use of atropine or other anticholinergic agents should be avoided.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
