INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60001, 'Iron', 'Cefdinir', 'Moderate', 'INTERVAL: The oral bioavailability of cefdinir may be significantly decreased by concomitant administration of iron preparations. The proposed mechanism is chelation of cefdinir by the iron cation, forming a complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'To minimize potential interaction with iron, the product labeling recommends that cefdinir be taken at least 2 hours before or 2 hours after administration of iron-containing products.', NULL, 'To minimize potential interaction with iron, the product labeling recommends that cefdinir be taken at least 2 hours before or 2 hours after administration of iron-containing products. However, this separation time may not be sufficient to prevent the interaction when sustained-release iron products are used, especially in higher dosages. Patients should be advised that reddish stools may occur due to formation of a nonabsorbable complex between cefdinir or its breakdown products and iron in the gastrointestinal tract.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60083/', '[1] "Product Information. Omnicef (cefdinir)." Parke-Davis, Morris Plains, NJ.[2] Ueno K, Tanaka K, Tsujimura K, Morishima Y, Iwashige H, Yamazaki K, Nakata I "Impairment of cefdinir absorption by iron ion." Clin Pharmacol Ther 54 (1993): 473-5', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60002, 'Chloramphenicol', 'Cefdinir', 'Moderate', 'Limited, primarily in vitro, data suggest that chloramphenicol may antagonize the bactericidal activity of cephalosporins against certain clinical isolates of gram-negative rods, group B streptococci, and Staphylococcus aureus. This antagonism appears to occur against strains for which chloramphenicol is bacteriostatic, and has been demonstrated with cefoperazone, cefotaxime, and ceftriaxone. The proposed mechanism is inhibition of protein synthesis by chloramphenicol, resulting in less protein substrate for cephalosporins to act on as inhibitors of bacterial cell wall synthesis.', NULL, 'The manufacturers recommend to avoid concomitant use.', NULL, 'The manufacturers recommend to avoid concomitant use . However, if concurrent administration cannot be avoided, the possibility of antagonism should be considered, and patients should be monitored for altered therapeutic effect.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60084/', '[1] Brown TH, Alford RH "Antagonism by chloramphenicol of broad-spectrum beta-lactam antibiotics against Klebsiella pneumoniae." Antimicrob Agents Chemother 25 (1984): 405-7[2] Asmar BI, Prainito M, Dajani AS "Antagonistic effect of chloramphenicol in combination with cefotaxime or ceftriaxone." Antimicrob Agents Chemother 32 (1988): 1375-8[3] Agencia Española de Medicamentos y Productos Sanitarios Healthcare "Centro de información online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[4] Brown TH, Alford RH "Failure of chloramphenicol and cefotaxime therapy in Klebsiella meningitis: possible role of antibiotic antagonism." South Med J 78 (1985): 869-71[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] French GL, Ling TK, Davies DP, Leung DT "Antagonism of ceftazidime by chloramphenicol in vitro and in vivo during treatment of gram negative meningitis." Br Med J (Clin Res Ed) 291 (1985): 636-7[8] "Product Information. Lorabid (loracarbef)." Lilly, Eli and Company, Indianapolis, IN.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60003, 'Cefdinir', 'Vibrio cholerae CVD 103-HgR strain live antigen (live)', 'Major', 'INTERVAL: The use of live, attenuated cholera vaccine with systemic antibiotics may result in a diminished immunologic response to the vaccine. Some antibiotics may be active against the vaccine strain of Vibrio cholerae, thereby preventing a sufficient degree of multiplication to occur in order to induce a protective immune response.', NULL, 'Live, attenuated cholera vaccine should not be administered during or for at least 14 days after treatment with systemic antibiotics.', NULL, 'Live, attenuated cholera vaccine should not be administered during or for at least 14 days after treatment with systemic antibiotics.', 'Antagonism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60085/', '[1] "Product Information. Vaxchora (cholera vaccine, live)." PaxVax, Miami, FL.[2] "Product Information. Vaxchora (cholera vaccine, live)." PaxVax, Miami, FL.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60004, 'Dicoumarol', 'Cefdinir', 'Moderate', 'Cephalosporins may potentiate the anticoagulant effects of vitamin K antagonists. Possible mechanisms include inhibited growth of vitamin K-producing intestinal bacteria, inhibited production of vitamin K-dependent clotting factors via a methylthiotetrazole (MTT) side chain (e.g., cefamandole, cefmetazole, cefoperazone, cefotetan), and/or inhibited platelet activity.', NULL, 'Prothrombin time and INR should be monitored and the patient closely observed for signs of bleeding.', NULL, 'Prothrombin time and INR should be monitored and the patient closely observed for signs of bleeding. The anticoagulant dose may be adjusted as indicated. Patients should be advised to notify their physicians if they experience any signs or symptoms that may indicate excessive anticoagulation, such as unusual or prolonged bleeding, bruising, coffee ground emesis, change in stool or urine color, headache, dizziness, or weakness.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60086/', '[1] "Product Information. Suprax (cefixime)." Lupin Pharmaceuticals Inc, Baltimore, MD.[2] Wood T, Johnson K, Naylor S, Weinshilboum R "Cefazolin administration and 2-methyl-1,3,4-thiadiazole-5-thiol in humantissue: possible relationship to hypoprothrombinemia." Drug Metab Dispos 30 (2002): 1123-1128[3] Natelson EA, Brown CH, 3d Bradshaw MW, Alfrey CP, Jr Williams TW, Jr "Influence of cephalosporin antibiotics on blood coagulation and platelet function." Antimicrob Agents Chemother 9 (1976): 91-3[4] Chen LJ, Hsiao FY, et al "Use of Hypoprothrombinemia-Inducing Cephalosporins and th