INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60147, 'Cefotaxime', 'Torasemide', 'Moderate', 'Limited data suggest that furosemide and possibly other loop diuretics may potentiate the nephrotoxicity of some cephalosporins. The exact mechanism of interaction is unknown, although furosemide has been shown to increase the plasma concentrations and/or reduce the clearance of several cephalosporins such as cephaloridine and ceftazidime.', NULL, 'Although data are primarily limited to cephaloridine, which is no longer commercially marketed, caution may be advisable in patients receiving a loop diuretic in combination with other cephalosporins.', NULL, 'Although data are primarily limited to cephaloridine, which is no longer commercially marketed, caution may be advisable in patients receiving a loop diuretic in combination with other cephalosporins. Renal function should be monitored, particularly when high dosages are used or when these medications are administered in the elderly or patients with preexisting renal impairment.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60229/', '[1] Norrby R, Stenqvist K, Elgefors B "Interaction between cephaloridine and furosemide in man." Scand J Infect Dis 8 (1976): 209-12[2] Chrysos G, Gargalianos P, Lelekis M, Stefanou J, Kosmidis J "Pharmacokinetic interactions of ceftazidime and frusemide." J Chemother 7 Suppl (1995): 107-10[3] Dodds MG, Foord RD "Enhancement by potent diuretics of renal tubular necrosis induced by cephaloridine." Br J Pharmacol 40 (1970): 227-36[4] Simpson IJ "Nephrotoxicity and acute renal failure associated with cephalothin and cephaloridine." N Z Med J 74 (1971): 312-5[5] Korn A, Eichler HG, Gasic S "A drug interaction study of ceftriaxone and frusemide in healthy volunteers." Int J Clin Pharmacol Ther Toxicol 24 (1986): 262-4[6] Trollfors B, Norrby R, Kristianson K, Nilsson NJ "Effects on renal function of treatment with cefoxitin alone or in combination with furosemide." Scand J Infect Dis 13 (1978): 73-7[7] Lawson DH, Macadam RF, Singh H, et al "Effect of furosemide on antibiotic-induced renal damage in rats." J Infect Dis 126 (1972): 593-600[8] Tilstone WJ, Semple PF, Lawson DH, Boyle JA "Effects of furosemide on glomerular filtration rate and clearance of practolol, digoxin, cephaloridine, and gentamicin." Clin Pharmacol Ther 22 (1977): 389-94[9] "Product Information. Lorabid (loracarbef)." Lilly, Eli and Company, Indianapolis, IN.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60148, 'Cefotaxime', 'Typhoid vaccine (live)', 'Major', 'INTERVAL: The use of live, attenuated oral typhoid vaccine with antibacterial sulfonamides or other antibiotics may result in a diminished immunologic response to the vaccine. Some antibiotics may be active against the vaccine strain of Salmonella typhi (Ty21a), thereby preventing a sufficient degree of multiplication to occur in order to induce a protective immune response.', NULL, 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics.', NULL, 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics. A longer interval should be considered following treatment with long-acting antibiotics (e.g., azithromycin). If malaria prophylaxis is needed, the same 3-day interval at the minimum should be observed between antimalarials and the vaccine. However, chloroquine, mefloquine, and pyrimethamine-sulfadoxine may be given concomitantly with the vaccine. Alternatively, parenteral typhoid vaccine (typhoid Vi polysaccharide vaccine) may be considered if coadministration with antibacterial agents is required.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60230/', '[1] UK government "Typhoid. Available from: URL: https://www.gov.uk/government/uploads/system/uploads/attachment_data/file/148512/Green-Book-Chapter-33-dh_125348.pdf." ([cited 2014]):[2] American Medical Association, Division of Drugs and Toxicology "Drug evaluations annual 1994." Chicago, IL: American Medical Association; (1994):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] CDC. Centers for Disease Control and Prevention "Typhoid & Paratyphoid Fever. Available from: URL: http://wwwnc.cdc.gov/travel/yellowbook/2014/chapter-3-infectious-diseases-related-to-travel/typhoid-and-paratyphoid-fever." ([2013]):', 1767363022);
