INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60168, 'Cefotetan', 'Rifampicin', 'Major', 'Coadministration of rifampin with other antibiotics which cause vitamin K dependent coagulopathy, such as cephalosporins with the N-methyl-thiotetrazole (NMTT) side chain, may increase the risk of severe coagulation disorders, which may result in fatal outcomes (particularly with high doses). The mechanism for this interaction has not been clearly delineated; however, proposed mechanisms include inhibited growth of vitamin K-producing intestinal bacteria, inhibited production of vitamin K-dependent clotting factors, interference with the biosynthesis of prothrombin, and/or inhibited platelet activity. Risk factors may include the elderly, renal or hepatic impairment, poor nutritional state, protracted antimicrobial therapy, and chronic anticoagulation therapy.', NULL, 'The concomitant use of rifampin with other antibiotics which cause vitamin K dependent coagulopathy, such as cephalosporins, should be avoided.', NULL, 'The concomitant use of rifampin with other antibiotics which cause vitamin K dependent coagulopathy, such as cephalosporins, should be avoided. If coadministration is unavoidable, monitoring of prothrombin time and INR should be considered and the patient should be observed for signs of bleeding. Patients should be advised to notify their physicians if they experience any signs or symptoms that may indicate excessive anticoagulation, such as unusual or prolonged bleeding, bruising, coffee ground emesis, change in stool or urine color, headache, dizziness, or weakness.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60250/', '[1] A