INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60201, 'Aluminum hydroxide', 'Cefpodoxime', 'Moderate', 'INTERVAL: Coadministration with antacids or agents with acid-neutralizing effects may reduce the oral bioavailability of cefpodoxime proxetil and cefuroxime axetil. The proposed mechanism is a pH-dependent reduction in drug dissolution and absorption.', NULL, 'Until further data are available, patients treated with cefpodoxime proxetil or cefuroxime axetil should avoid taking antacids or oral medications that contain antacids (e.', NULL, 'Until further data are available, patients treated with cefpodoxime proxetil or cefuroxime axetil should avoid taking antacids or oral medications that contain antacids (e.g., didanosine buffered tablets or pediatric oral solution) for at least two hours before and after administration of the antibiotic.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60283/', '[1] Honig PK, Gillespie BK "Clinical significance of pharmacokinetic drug interactions with over-the-counter (OTC) drugs." Clin Pharmacokinet 35 (1998): 167-71[2] Saathoff N, Lode H, Neider K, et al "Pharmacokinetics of cefpodoxime and interactions with an antacid and an H2 receptor antagonist." Antimicrob Agents Chemother 36 (1992): 796-800[3] Hughes GS, Heald DL, Barker KB, et al. "The effects of gastric pH and food on the pharmacokinetics of a new oral cephalosporin, cefpodoxime proxetil." Clin Pharmacol Ther 46 (1989): 674-85[4] Sommers DK, van Wyk M, Moncrieff J, Schoeman HS "Influence of food and reduced gastric acidity on the bioavailability of bacampicillin and cefuroxime axetil." Br J Clin Pharmacol 18 (1984): 535-9', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60202, 'Amikacin (liposome)', 'Cefpodoxime', 'Moderate', 'Coadministration of aminoglycosides and cephalosporins may increase the risk of nephrotoxicity. An increased incidence of nephrotoxicity has been reported during concomitant use of aminoglycosides and some, mostly older cephalosporins (e.g., cefaloridine, cefamandole, cefazolin, cefotaxime, cefoxitin, ceftazidime, cefuroxime, cephalothin, ceftriaxone). The risk may be greatest in the elderly or patients with preexisting renal impairment, when large doses are used, and during prolonged treatment.', NULL, 'The lowest effective dosages of aminoglycosides and cephalosporins should be used when they are prescribed in combination.', NULL, 'The lowest effective dosages of aminoglycosides and cephalosporins should be used when they are prescribed in combination. Renal function should be monitored closely. The same precaution may be applicable when aminoglycosides are administered via irrigation, intrapleurally, intraperitoneally or orally, since aminoglycosides can be systemically absorbed via these routes; however, clinical data are lacking.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60284/', '[1] Yasuhara H, Kobayashi S, Sakamoto K, Kamijo K "Pharmacokinetics of amikacin and cephalothin in bedridden elderly patients." J Clin Pharmacol 22 (1982): 403-9[2] Engle JE, Drago J, Carlin B, Schoolwerth AC "Letter: Reversible acute renal failure after cephalothin." Ann Intern Med 83 (1975): 232-3[3] Kabins SA, Cohen S "Cephalothin serum levels in the azotemic patient." Antimicrob Agents Chemother 10 (1964): 207-14[4] Gonzalez-Vitale JC, h