INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60249, 'Entecavir', 'Cefprozil', 'Moderate', 'Coadministration of entecavir with another drug that is also eliminated by active tubular secretion may result in increased plasma concentrations of one or both drugs due to competitive inhibition of transporters in the renal tubules. Drugs (and/or their metabolites) that are thought to undergo active tubular secretion include acyclovir, allopurinol, aminosalicylic acid, cidofovir, cimetidine, creatine, dyphylline, famciclovir, famotidine, flecainide, ganciclovir, levetiracetam, metformin, methotrexate, midodrine, mycophenolic acid, oseltamivir, pralatrexate, probenecid, procainamide, quinidine, ranitidine, tenofovir, triamterene, trimethoprim, valacyclovir, valganciclovir, zalcitabine, zidovudine, and many of the beta-lactam and quinolone antibiotics.', NULL, 'Patients receiving entecavir with another drug that undergoes active tubular secretion should be monitored for excessive pharmacologic effects of both drugs, and the dosages adjusted as necessary.', NULL, 'Patients receiving entecavir with another drug that undergoes active tubular secretion should be monitored for excessive pharmacologic effects of both drugs, and the dosages adjusted as necessary.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60331/', '[1] "Product Information. Baraclude (entecavir)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Baraclude (entecavir)." Bristol-Myers Squibb, Princeton, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60250, 'Etacrynic acid', 'Cefprozil', 'Moderate', 'Limited data suggest that furosemide and possibly other loop diuretics may potentiate the nephrotoxicity of some cephalosporins. The exact mechanism of interaction is unknown, although furosemide has been shown to increase the plasma concentrations and/or reduce the clearance of several cephalosporins such as cephaloridine and ceftazidime.', NULL, 'Although data are primarily limited to cephaloridine, which is no longer commercially marketed, caution may be advisable in patients receiving a loop diuretic in combination with other cephalosporins.', NULL, 'Although data are primarily limited to cephaloridine, which is no longer commercially marketed, caution may be advisable in patients receiving a loop diuretic in combination with other cephalosporins. Renal function should be monitored, particularly when high dosages are used or when these medications are administered in the elderly or patients with preexisting renal impairment.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60332/', '[1] Norrby R, Stenqvist K, Elgefors B "Interaction between cephaloridine and furosemide in man." Scand J Infect Dis 8 (1976): 209-12[2] Chrysos G, Gargalianos P, Lelekis M, Stefanou J, Kosmidis J "Pharmacokinetic interactions of ceftazidime and frusemide." J Chemother 7 Suppl (1995): 107-10[3] Dodds MG, Foord RD "Enhancement by potent diuretics of renal tubular necrosis induced by cephaloridine." Br J Pharmacol 40 (1970): 227-36[4] Simpson IJ "Nephrotoxicity and acute renal failure associated with cephalothin and cephaloridine." N Z Med J 74 (1971): 312-5[5] Korn A, Eichler HG, Gasic S "A drug interaction study of ceftriaxone and frusemide in healthy volunteers." Int J Clin Pharmacol Ther Toxicol 24 (1986): 262-4[6] Trollfors B, Norrby R, Kristianson K, Nilsson NJ "Effects on renal function of treatment with cefoxitin alone or in combination with furosemide." Scand J Infect Dis 13 (1978): 73-7[7] Lawson DH, Macadam RF, Singh H, et al "Effect of furosemide on antibiotic-induced renal damage in rats." J Infect Dis 126 (1972): 593-600[8] Tilstone WJ, Semple PF, Lawson DH, Boyle JA "Effects of furosemide on glomerular filtration rate and clearance of practolol, digoxin, cephaloridine, and gentamicin." Clin Pharmacol Ther 22 (1977): 389-94[9] "Product Information. Lorabid (loracarbef)." Lilly, Eli and Company, Indianapolis, IN.', 1767363022);
