INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60383, 'Ceftriaxone', 'Leucovorin', 'Major', 'Coadministration of ceftriaxone with calcium-containing solutions, even via different infusion lines, may cause precipitation of ceftriaxone-calcium salt. There have been cases of fatal reactions involving the presence of these precipitates in the lung and kidneys of both term and premature neonates as well as infants up to 1 year of age.', NULL, 'Ceftriaxone must not be mixed or administered simultaneously with intravenous calcium-containing solutions (e.', NULL, 'Ceftriaxone must not be mixed or administered simultaneously with intravenous calcium-containing solutions (e.g., Ringer''s or Hartmann''s solution; parenteral nutrition containing calcium) via a Y-site in any patient regardless of age. Concomitant use of ceftriaxone and intravenous calcium-containing products (or within 48 hours of each other) is considered contraindicated in neonates 28 days of age or younger, even when administered via different infusion lines at different sites. Ceftriaxone should not be used in neonates if they are receiving, or are expected to receive, calcium-containing intravenous products. In patients older than 28 days, ceftriaxone and calcium-containing products may be administered sequentially, provided the infusion lines are thoroughly flushed between infusions with a compatible fluid. No data or recommendations are available for concomitant use of intravenous ceftriaxone and oral calcium-containing products or intramuscular ceftriaxone and calcium-containing products (IV or oral).', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60468/', '[1] CDER. Center 