INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60407, 'Didanosine', 'Cefuroxime', 'Moderate', 'INTERVAL: Coadministration with antacids or agents with acid-neutralizing effects may reduce the oral bioavailability of cefpodoxime proxetil and cefuroxime axetil. The proposed mechanism is a pH-dependent reduction in drug dissolution and absorption.', NULL, 'Until further data are available, patients treated with cefpodoxime proxetil or cefuroxime axetil should avoid taking antacids or oral medications that contain antacids (e.', NULL, 'Until further data are available, patients treated with cefpodoxime proxetil or cefuroxime axetil should avoid taking antacids or oral medications that contain antacids (e.g., didanosine buffered tablets or pediatric oral solution) for at least two hours before and after administration of the antibiotic.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60493/', '[1] Honig PK, Gillespie BK "Clinical significance of pharmacokinetic drug interactions with over-the-counter (OTC) drugs." Clin Pharmacokinet 35 (1998): 167-71[2] Saathoff N, Lode H, Neider K, et al "Pharmacokinetics of cefpodoxime and interactions with an antacid and an H2 receptor antagonist." Antimicrob Agents Chemother 36 (1992): 796-800[3] Hughes GS, Heald DL, Barker KB, et al. "The effects of gastric pH and food on the pharmacokinetics of a new oral cephalosporin, cefpodoxime proxetil." Clin Pharmacol Ther 46 (1989): 674-85[4] Sommers DK, van Wyk M, Moncrieff J, Schoeman HS "Influence of food and reduced gastric acidity on the bioavailability of bacampicillin and cefuroxime axetil." Br J Clin Pharmacol 18 (1984): 535-9', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60408, 'Disulfiram', 'Cefuroxime', 'Moderate', 'An isolated case report suggests that the disulfiram reaction can occur during coadministration of cefuroxime axetil. Because the axetil moiety is metabolized to acetaldehyde in vivo, the reaction was presumably caused by an accumulation of acetaldehyde in the presence of disulfiram, which blocks the conversion of acetaldehyde to acetate. High levels of acetaldehyde can produce an unpleasant physiologic response referred to as the ''disulfiram reaction''. Symptoms include flushing, throbbing in head and neck, throbbing headache, respiratory difficulty, nausea, vomiting, sweating, thirst, chest pain, palpitation, dyspnea, hyperventilation, tachycardia, hypotension, syncope, weakness, vertigo, blurred vision, and confusion.', NULL, 'Until more information is available, the possibility of a disulfiram reaction should be considered when prescribing cefuroxime axetil to patients treated with disulfiram.', NULL, 'Until more information is available, the possibility of a disulfiram reaction should be considered when prescribing cefuroxime axetil to patients treated with disulfiram.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60494/', '[1] Mattes JA "Antabuse reaction with ceftin." Am J Psychiatry 163 (2006): 2019-20[2] "Product Information. Ceftin (cefuroxime)." Glaxo Wellcome, Research Triangle Park, NC.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60409, 'Entecavir', 'Cefuroxime', 'Moderate', 'Coadministration of entecavir with another drug that is also eliminated by active tubular secretion may result in increased plasma concentrations of one or both drugs due to competitive inhibition of transporters in the renal tubules. Drugs (and/or their metabolites) that are thought to undergo active tubular secretion include acyclovir, allopurinol, aminosalicylic acid, cidofovir, cimetidine, creatine, dyphylline, famciclovir, famotidine, flecainide, ganciclovir, levetiracetam, metformin, methotrexate, midodrine, mycophenolic acid, oseltamivir, pralatrexate, probenecid, procainamide, quinidine, ranitidine, tenofovir, triamterene, trimethoprim, valacyclovir, valganciclovir, zalcitabine, zidovudine, and many of the beta-lactam and quinolone antibiotics.', NULL, 'Patients receiving entecavir with another drug that undergoes active tubular secretion should be monitored for excessive pharmacologic effects of both drugs, and the dosages adjusted as necessary.', NULL, 'Patients receiving entecavir with another drug that undergoes active tubular secretion should be monitored for excessive pharmacologic effects of both drugs, and the dosages adjusted as necessary.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60495/', '[1] "Product Information. Baraclude (entecavir)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Baraclude (entecavir)." Bristol-Myers Squibb, Princeton, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60410, 'Esomeprazole', 'Cefuroxime', 'Moderate', 'The coadministration with H2-receptor antagonists, proton pump inhibitors, or other agents that can increase gastric pH may reduce the oral bioavailability of cefpodoxime proxetil and cefuroxime axetil. The proposed mechanism is a pH-dependent reduction in drug dissolution and absorption.', NULL, 'Until further data are available, patients treated with cefpodoxime proxetil or cefuroxime axetil may want to avoid using H2-receptor antagonists, proton pump inhibitors, or other agents that can increase gastric pH.', NULL, 'Until further data are available, patients treated with cefpodoxime proxetil or cefuroxime axetil may want to avoid using H2-receptor antagonists, proton pump inhibitors, or other agents that can increase gastric pH. An alternative antibiotic may be considered if these medications cannot be discontinued.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60496/', '[1] Hughes GS, Heald DL, Barker KB, et al. "The effects of gastric pH and food on the pharmacokinetics of a new oral cephalosporin, cefpodoxime proxetil." Clin Pharmacol Ther 46 (1989): 674-85[2] Sommers DK, van Wyk M, Moncrieff J, Schoeman HS "Influence of food and reduced gastric acidity on the bioavailability of bacampicillin and cefuroxime axetil." Br J Clin Pharmacol 18 (1984): 535-9[3] Saathoff N, Lode H, Neider K, et al "Pharmacokinetics of cefpodoxime and interactions with an antacid and an H2 receptor antagonist." Antimicrob Agents Chemother 36 (1992): 796-800[4] Honig PK, Gillespie BK "Clinical significance of pharmacokinetic drug interactions with over-the-counter (OTC) drugs." Clin Pharmacokinet 35 (1998): 167-71', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60411, 'Etacrynic acid', 'Cefuroxime', 'Moderate', 'Limited data suggest that furosemide and possibly other loop diuretics may potentiate the nephrotoxicity of some cephalosporins. The exact mechanism of interaction is unknown, although furosemide has been shown to increase the plasma concentrations and/or reduce the clearance of several cephalosporins such as cephaloridine and ceftazidime.', NULL, 'Although data are primarily limited to cephaloridine, which is no longer commercially marketed, caution may be advisable in patients receiving a loop diuretic in combination with other cephalosporins.', NULL, 'Although data are primarily limited to cephaloridine, which is no longer commercially marketed, caution may be advisable in patients receiving a loop diuretic in combination with other cephalosporins. Renal function should be monitored, particularly when high dosages are used or when these medications are administered in the elderly or patients with preexisting renal impairment.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60497/', '[1] Norrby R, Stenqvist K, Elgefors B "Interaction between cephaloridine and furosemide in man." Scand J Infect Dis 8 (1976): 209-12[2] Chrysos G, Gargalianos P, Lelekis M, Stefanou J, Kosmidis J "Pharmacokinetic interactions of ceftazidime and frusemide." J Chemother 7 Suppl (1995): 107-10[3] Dodds MG, Foord RD "Enhancement by potent diuretics of renal tubular necrosis induced by cephaloridine." Br J Pharmacol 40 (1970): 227-36[4] Simpson IJ "Nephrotoxicity and acute renal failure associated with cephalothin and cephaloridine." N Z Med J 74 (1971): 312