INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60427, 'Cefuroxime', 'Nizatidine', 'Moderate', 'The coadministration with H2-receptor antagonists, proton pump inhibitors, or other agents that can increase gastric pH may reduce the oral bioavailability of cefpodoxime proxetil and cefuroxime axetil. The proposed mechanism is a pH-dependent reduction in drug dissolution and absorption.', NULL, 'Until further data are available, patients treated with cefpodoxime proxetil or cefuroxime axetil may want to avoid using H2-receptor antagonists, proton pump inhibitors, or other agents that can increase gastric pH.', NULL, 'Until further data are available, patients treated with cefpodoxime proxetil or cefuroxime axetil may want to avoid using H2-receptor antagonists, proton pump inhibitors, or other agents that can increase gastric pH. An alternative antibiotic may be considered if these medications cannot be discontinued.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60513/', '[1] Hughes GS, Heald DL, Barker KB, et al. "The effects of gastric pH and food on the pharmacokinetics of a new oral cephalosporin, cefpodoxime proxetil." Clin Pharmacol Ther 46 (1989): 674-85[2] Sommers DK, van Wyk M, Moncrieff J, Schoeman HS "Influence of food and reduced gastric acidity on the bioavailability of bacampicillin and cefuroxime axetil." Br J Clin Pharmacol 18 (1984): 535-9[3] Saathoff N, Lode H, Neider K, et al "Pharmacokinetics of cefpodoxime and interactions with an antacid and an H2 receptor antagonist." Antimicrob Agents Chemother 36 (1992): 796-800[4] Honig PK, Gillespie BK "Clinical significance of pharmacokinetic drug interactions with over-the-counter (OTC) drugs." Clin Pharmacokinet 35 (1998): 167-71', 1767363022);
