INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60482, 'Celecoxib', 'Metoprolol', 'Moderate', 'Coadministration with celecoxib may increase the plasma concentrations of metoprolol. The mechanism is celecoxib inhibition of metoprolol metabolism via CYP450 2D6.', NULL, 'Although metoprolol has a wide therapeutic range, caution is advised during coadministration with celecoxib because hemodynamic effects of metoprolol may be related to plasma concentration.', NULL, 'Although metoprolol has a wide therapeutic range, caution is advised during coadministration with celecoxib because hemodynamic effects of metoprolol may be related to plasma concentration. Patients should be monitored for increased pharmacologic effects that may result in bradycardia or hypotension.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60568/', '[1] Bengtsson C, Johnsson G, Regardh C-G "Plasma levels and effects of metoprolol on blood pressure and heart rate in hypertensive patients after an acute dose and between two doses during long-term treatment." Clin Pharmacol Ther 17 (1975): 400-8[2] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.[3] Werner U, Werner D, Rau T, Fromm MF, Hinz B, Brune K "Celecoxib inhibits metabolism of cytochrome P450 2D6 substrate metoprolol in humans." Clin Pharmacol Ther 74 (2003): 130-137[4] Darcy PF "Nutrient-drug interactions." Adverse Drug React Toxicol Rev 14 (1995): 233-54[5] "Product Information. Lopressor (metoprolol)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60483, 'Celecoxib', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 may increase the plasma concentrations of celecoxib, which is primarily metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of celecoxib should be considered.', NULL, 'Clinical monitoring for altered effects of celecoxib is recommended following initiation or discontinuation of a CYP450 2C9 inhibitor.', NULL, 'Clinical monitoring for altered effects of celecoxib is recommended following initiation or discontinuation of a CYP450 2C9 inhibitor. Dosage adjustments may be required if an interaction is suspected. Patients should be advised to notify their physician if they experience potential signs and symptoms of celecoxib toxicity such as abdominal pain, black or tarry stools, nausea, vomiting, lethargy, fluid retention, swelling of legs, and worsening heart failure.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60569/', '[1] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60484, 'Celecoxib', 'Mifepristone', 'Moderate', 'Coadministration with mifepristone may increase the plasma concentrations of drugs that are substrates of CYP450 2C8 and/or 2C9. The mechanism is decreased clearance due to inhibition of CYP450 2C8/2C9 activity by mifepristone.', NULL, 'Caution is advised if mifepristone must be used concomitantly with medications that are substrates of CYP450 2C8 and/or 2C9, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if mifepristone must be used concomitantly with medications that are substrates of CYP450 2C8 and/or 2C9, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever mifepristone is added to or withdrawn from therapy. The lowest dosage of sensitive substrates (e.g., repaglinide, warfarin) should be used whenever possible. Because mifepristone is eliminated slowly from the body, drug interactions may be observed for a prolonged period following discontinuation (approximately 2 to 3 weeks if mifepristone had been administered chronically to steady state).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60570/', '[1] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[2] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[3] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.', 1767363022);
