INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (120599, 'Iopamidol', 'Penbutolol', 'Moderate', 'Limited data suggest that patients receiving beta-blockers may have an increased risk of severe hypotensive and/or hypersensitivity reactions to parenteral iodinated contrast media. In addition, allergic/anaphylactoid reactions in these patients may be more difficult to treat.', NULL, 'Patients who have received beta-blockers should be closely monitored for adverse reactions to iodinated contrast media.', NULL, 'Patients who have received beta-blockers should be closely monitored for adverse reactions to iodinated contrast media. If anaphylaxis occurs, clinicians should be aware that beta-blockers may attenuate the response to epinephrine. Thus, larger doses of epinephrine may be necessary to overcome the bronchospasm, although such large doses can also cause excessive alpha-adrenergic stimulation resulting in hypertension, reflex bradycardia, heart block, and possible potentiation of bronchospasm. Alternative treatments recommended include vigorous supportive care (e.g., fluids) and the use of parenteral beta-agonists for bronchospasm and norepinephrine for hypotension.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/120737/', '[1] "Product Information. Osmovist 240 (iotrolan)." Berlex Canada Inc, Lachine, QC.[2] Costa N "Understanding contrast media." J Infus Nurs 27 (2004): 302-12[3] "Product Information. Visipaque (iodixanol)." Nycomed Inc, Princeton, NJ.[4] "Product Information. Ultravist (iopromide)." Berlex, Richmond, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (120600, 'Iopamidol', 'Penicillamine', 'Major', 'Concomitant use of intravascular radiocontrast media with other nephrotoxic agents may potentiate the risk of contrast-induced nephropathy and renal impairment. While the condition is usually transient and asymptomatic, it can be associated with increased risk of renal failure, dialysis, prolonged hospitalization, significant long-term morbidity, and mortality.', NULL, 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy.', NULL, 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy. Any repeat procedures with contrast media, if necessary, should not occur until at least 48 to 72 hours after the previous contrast exposure and renal function has fully recovered.', 'Synergism', 'Auranofin, Aurothioglucose, Sodium aurothiomalate', 'Ethiodized oil, Iopanoic acid, Tyropanoic acid, Iopodic acid', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/120738/', '[1] Dubrovskaya Y, Prasad N, Lee Y, Esaian D, Figueroa DA, Tam VH "Risk factors for nephrotoxicity onset associated with polymyxin B therapy." J Antimicrob Chemother 70 (2015): 1903-7[2] Bentley ML, Corwin HL, Dasta J "Drug-induced acute kidney injury in the critically ill adult: recognition and prevention strategies." Crit Care Med 38(6 Suppl) (2010): S169-74[3] Kellum JA, Leblanc M, Venkataraman R "Acute renal failure." BMJ Clin Evid 9 (2008): 2001[4] van den Berk G, Tonino S, de Fijter C, Smit W, Schultz MJ "Bench-to-bedside