INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (120857, 'Itraconazole', 'Finasteride', 'Moderate', 'Coadministration with itraconazole may significantly increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by itraconazole.', NULL, 'Caution is advised if itraconazole must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if itraconazole must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever itraconazole is added to or withdrawn from therapy. Some authorities recommend avoiding concomitant use of nevirapine from 2 weeks before and during treatment with itraconazole.', 'Metabolism', 'Caspofungin, Micafungin, Amphotericin B, Miconazole, Posaconazole, Oteseconazole, Flucytosine, Anidulafungin', 'Minoxidil, Deoxycholic acid, Calcium gluconate, Magnesium sulfate, Glycopyrronium, Pimecrolimus, Aminobenzoic acid, Caffeine, Cromoglicic acid, Abrocitinib, Dupilumab, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/120995/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Kaukonen KM, Olkkola KT, Neuvonen PJ "Itraconazole increases plasma concentrations of quinidine." Clin Pharmacol Ther 62 (1997): 510-7[3] Auclair B, Berning SE, Huitt GA, Peloquin CP "Potential interaction between itraconazole and clarithromycin." Pharmacotherapy 19 (1999)